formula:
function calculate({ projectDurationDays, dailyOperatingCost, typicalDelayDaysPerEvent, avgSevereWeatherEventsPerYear, mitigationStrategyCost, mitigationEffectivenessPercent, assessmentPeriodYears }) {

  // Input validation and default handling
  const pd = Math.max(1, projectDurationDays || 1);
  const doc = Math.max(0, dailyOperatingCost || 0);
  const tdd = Math.max(0, typicalDelayDaysPerEvent || 0);
  const awe = Math.max(0, avgSevereWeatherEventsPerYear || 0);
  const msc = Math.max(0, mitigationStrategyCost || 0);
  const me = Math.max(0, Math.min(100, mitigationEffectivenessPercent || 0)) / 100;
  const ap = Math.max(1, assessmentPeriodYears || 1);

  // Edge case: If daily operating cost or typical delays are zero, there's no potential cost to mitigate.
  if (doc === 0 || tdd === 0 || awe === 0) {
    return {
      potentialAnnualDelayCost: 0,
      postMitigationAnnualDelayCost: 0,
      annualSavingsFromMitigation: -msc, // Still incur mitigation cost if no delays, so negative savings
      totalMitigationCostOverPeriod: msc * ap,
      totalSavingsOverPeriod: -msc * ap,
      roiPercentage: (msc > 0 ? -100 : 0),
      paybackPeriodYears: Infinity
    };
  }

  // 1. Calculate Potential Annual Delay Cost (without mitigation)
  const potentialAnnualDelayDays = tdd * awe;
  const potentialAnnualDelayCost = potentialAnnualDelayDays * doc;

  // 2. Calculate Post-Mitigation Annual Delay Days and Cost
  const reducedDelayDaysPerEvent = tdd * (1 - me);
  const postMitigationAnnualDelayDays = reducedDelayDaysPerEvent * awe;
  const postMitigationAnnualDelayCost = postMitigationAnnualDelayDays * doc;

  // 3. Calculate Annual Savings from Mitigation
  const annualSavingsFromMitigation = potentialAnnualDelayCost - postMitigationAnnualDelayCost;

  // 4. Calculate Total Mitigation Cost over Assessment Period
  const totalMitigationCostOverPeriod = msc * ap;

  // 5. Calculate Total Net Savings over Assessment Period
  const totalSavingsOverPeriod = (annualSavingsFromMitigation * ap) - totalMitigationCostOverPeriod;

  // 6. Calculate Return on Investment (ROI)
  let roiPercentage = 0;
  if (totalMitigationCostOverPeriod > 0) {
    roiPercentage = (totalSavingsOverPeriod / totalMitigationCostOverPeriod) * 100;
  } else if (totalSavingsOverPeriod > 0) {
    roiPercentage = Infinity; // Infinite ROI if cost is zero and there are savings
  } else {
    roiPercentage = 0; // If both are zero, or negative savings with zero cost, ROI is 0
  }

  // 7. Calculate Payback Period
  let paybackPeriodYears = Infinity;
  if (annualSavingsFromMitigation > 0) {
    paybackPeriodYears = msc / annualSavingsFromMitigation;
  } else if (annualSavingsFromMitigation <= 0 && msc > 0) {
    paybackPeriodYears = Infinity; // Will never pay back if no savings or negative savings
  } else if (msc === 0) {
    paybackPeriodYears = 0; // No cost, immediate payback
  }

  return {
    potentialAnnualDelayCost,
    postMitigationAnnualDelayCost,
    annualSavingsFromMitigation,
    totalMitigationCostOverPeriod,
    totalSavingsOverPeriod,
    roiPercentage,
    paybackPeriodYears: isFinite(paybackPeriodYears) ? Math.max(0, paybackPeriodYears) : Infinity // Ensure non-negative
  };
}
