function calculate() {
    const currentLoanBalance = parseFloat(inputs.currentLoanBalance);
    const currentInterestRate = parseFloat(inputs.currentInterestRate) / 100;
    const currentLoanTermRemaining = parseInt(inputs.currentLoanTermRemaining);
    const newInterestRate = parseFloat(inputs.newInterestRate) / 100;
    const newLoanTerm = parseInt(inputs.newLoanTerm);
    const newLoanFees = parseFloat(inputs.newLoanFees) || 0;

    // --- Input Validations ---
    if (isNaN(currentLoanBalance) || currentLoanBalance <= 0) { throw new Error("Current Loan Balance must be a positive number."); }
    if (isNaN(currentInterestRate) || currentInterestRate < 0) { throw new Error("Current Annual Interest Rate must be a non-negative number."); }
    if (isNaN(currentLoanTermRemaining) || currentLoanTermRemaining <= 0) { throw new Error("Current Loan Term Remaining must be a positive integer."); }
    if (isNaN(newInterestRate) || newInterestRate < 0) { throw new Error("Proposed New Annual Interest Rate must be a non-negative number."); }
    if (isNaN(newLoanTerm) || newLoanTerm <= 0) { throw new Error("Proposed New Loan Term must be a positive integer."); }
    if (isNaN(newLoanFees) || newLoanFees < 0) { throw new Error("Refinance Fees must be a non-negative number."); }

    // --- Helper Functions ---
    const calculateMonthlyPayment = (principal, annualRate, termMonths) => {
        if (principal <= 0 || termMonths <= 0) return 0;
        if (annualRate === 0) {
            return principal / termMonths;
        }
        const monthlyRate = annualRate / 12;
        if (monthlyRate < 1e-9) return principal / termMonths; // Handle very small rates effectively as 0

        const exponent = Math.pow(1 + monthlyRate, termMonths);
        return principal * (monthlyRate * exponent) / (exponent - 1);
    };

    const generateAmortizationSchedule = (principal, annualRate, termMonths) => {
        const schedule = [];
        let remainingBalance = principal;
        const monthlyRate = annualRate / 12;
        const monthlyPayment = calculateMonthlyPayment(principal, annualRate, termMonths);
        let totalInterestAccrued = 0;

        for (let month = 1; month <= termMonths; month++) {
            let interestPayment;
            let principalPayment;

            if (remainingBalance <= 0) {
                interestPayment = 0;
                principalPayment = 0;
            } else if (annualRate === 0) {
                interestPayment = 0;
                principalPayment = monthlyPayment;
            } else {
                interestPayment = remainingBalance * monthlyRate;
                principalPayment = monthlyPayment - interestPayment;
            }

            // Adjust last payment to precisely zero out the balance and avoid over/under payment
            if (month === termMonths) {
                principalPayment = remainingBalance; // Pay off the exact remaining balance
                interestPayment = monthlyPayment - principalPayment; // Calculate interest for this last payment
                if (interestPayment < 0) interestPayment = 0; // Interest cannot be negative
                remainingBalance = 0; // Loan is paid off
            } else {
                 remainingBalance -= principalPayment;
                 // Ensure remaining balance doesn't go negative due to floating point inaccuracies
                 if (remainingBalance < 1e-9 && remainingBalance > -1e-9) remainingBalance = 0; 
            }

            totalInterestAccrued += interestPayment;

            schedule.push({
                month: month,
                payment: monthlyPayment,
                principalPayment: principalPayment,
                interestPayment: interestPayment,
                remainingBalance: remainingBalance
            });
        }
        return { schedule, totalInterestPaid: totalInterestAccrued };
    };

    // --- Calculations ---

    // 1. Current Loan Analysis
    const calculatedCurrentMonthlyPayment = calculateMonthlyPayment(currentLoanBalance, currentInterestRate, currentLoanTermRemaining);
    const { schedule: currentAmortizationSchedule, totalInterestPaid: currentTotalInterest } = 
        generateAmortizationSchedule(currentLoanBalance, currentInterestRate, currentLoanTermRemaining);

    // 2. New Loan Analysis
    const newLoanPrincipal = currentLoanBalance + newLoanFees;
    const newMonthlyPayment = calculateMonthlyPayment(newLoanPrincipal, newInterestRate, newLoanTerm);
    const { schedule: newAmortizationSchedule, totalInterestPaid: newTotalInterest } = 
        generateAmortizationSchedule(newLoanPrincipal, newInterestRate, newLoanTerm);

    // 3. Savings Calculation
    const monthlySavings = calculatedCurrentMonthlyPayment - newMonthlyPayment;
    const totalInterestSaved = currentTotalInterest - newTotalInterest;

    // 4. Break-even Point for fees
    let breakEvenPoint = 0;
    if (newLoanFees > 0) {
        if (monthlySavings > 0) {
            breakEvenPoint = newLoanFees / monthlySavings;
        } else {
            breakEvenPoint = Infinity; // Will never break even if no monthly savings or negative savings
        }
    }

    return {
        calculatedCurrentMonthlyPayment: calculatedCurrentMonthlyPayment,
        currentTotalInterest: currentTotalInterest,
        newMonthlyPayment: newMonthlyPayment,
        newTotalInterest: newTotalInterest,
        monthlySavings: monthlySavings,
        totalInterestSaved: totalInterestSaved,
        breakEvenPoint: breakEvenPoint,
        currentAmortizationSchedule: currentAmortizationSchedule,
        newAmortizationSchedule: newAmortizationSchedule
    };
}
