const { hwt, cwt, wbt, gpm } = inputs; 

if (hwt === null || cwt === null || wbt === null || gpm === null || isNaN(hwt) || isNaN(cwt) || isNaN(wbt) || isNaN(gpm)) {
    return { error: 'Please fill in all input fields with valid numbers.' };
}

if (wbt < -50 || wbt > 120) return { error: 'Wet-bulb temperature (WBT) must be between -50°F and 120°F.' };
if (cwt < wbt) return { error: 'Cold water temperature (CWT) cannot be less than the ambient wet-bulb temperature (WBT). Check your inputs.' };
if (hwt < cwt) return { error: 'Hot water temperature (HWT) cannot be less than the cold water temperature (CWT). Check your inputs.' };
if (gpm <= 0) return { error: 'Water flow rate (GPM) must be a positive value.' };

// Calculations
const approach = cwt - wbt;
const range = hwt - cwt;

// Heat Load (BTU/hr) = GPM * 500 * Range (500 is approx. factor for water: 8.33 lb/gal * 60 min/hr * 1 BTU/lb°F)
const heat_load = gpm * 500 * range;

// Evaporation Rate (GPM) based on latent heat of vaporization
// Formula: Heat Load (BTU/hr) / (Latent Heat of Vaporization * Density of Water * 60 min/hr)
// Assuming Latent Heat of Vaporization = 1050 BTU/lb, Density of Water = 8.33 lb/gal
// Constant denominator = 1050 * 8.33 * 60 = 524790
const evaporation_rate = heat_load / 524790;

// Cooling Tower Tons (Refrigeration Tons, 1 TR = 12,000 BTU/hr)
const cooling_tower_tons = heat_load / 12000;

return {
  approach,
  range,
  heat_load,
  evaporation_rate,
  cooling_tower_tons
};
