function calculate({ initialInvestment, annualOperationalSavings, annualEmissionReduction, costOfCarbon, projectLifespan, discountRate, annualCostEscalation, carbonPriceEscalation }) {

    initialInvestment = parseFloat(initialInvestment);
    annualOperationalSavings = parseFloat(annualOperationalSavings);
    annualEmissionReduction = parseFloat(annualEmissionReduction);
    costOfCarbon = parseFloat(costOfCarbon);
    projectLifespan = parseInt(projectLifespan);
    discountRate = parseFloat(discountRate);
    annualCostEscalation = parseFloat(annualCostEscalation || 0);
    carbonPriceEscalation = parseFloat(carbonPriceEscalation || 0);

    const discountRateDecimal = discountRate / 100;
    const annualCostEscalationDecimal = annualCostEscalation / 100;
    const carbonPriceEscalationDecimal = carbonPriceEscalation / 100;

    let totalFinancialBenefit = 0;
    let npv = 0;
    const annualCashFlows = [];

    for (let year = 1; year <= projectLifespan; year++) {
        const currentAnnualOperationalSavings = annualOperationalSavings * Math.pow(1 + annualCostEscalationDecimal, year - 1);
        const currentCostOfCarbon = costOfCarbon * Math.pow(1 + carbonPriceEscalationDecimal, year - 1);
        const annualMonetaryCarbonBenefit = annualEmissionReduction * currentCostOfCarbon;
        const annualGrossCashFlow = currentAnnualOperationalSavings + annualMonetaryCarbonBenefit;

        totalFinancialBenefit += annualGrossCashFlow;
        annualCashFlows.push(annualGrossCashFlow);

        const discountedCashFlow = annualGrossCashFlow / Math.pow(1 + discountRateDecimal, year);
        npv += discountedCashFlow;
    }

    npv -= initialInvestment;

    // Calculate Simple Payback Period
    let paybackPeriod = Infinity;
    if (initialInvestment <= 0) {
        paybackPeriod = 0;
    } else {
        const effectiveAnnualBenefitYear1 = annualOperationalSavings + (annualEmissionReduction * costOfCarbon);
        if (effectiveAnnualBenefitYear1 > 0) {
            paybackPeriod = initialInvestment / effectiveAnnualBenefitYear1;
            // Cap payback period to project lifespan if it exceeds it
            if (paybackPeriod > projectLifespan) {
                paybackPeriod = Infinity; // Or projectLifespan if preferred to indicate no full payback within project life
            }
        } else {
            paybackPeriod = Infinity;
        }
    }

    // Calculate ROI Percentage
    let roiPercentage = 0;
    if (initialInvestment === 0) {
        roiPercentage = totalFinancialBenefit > 0 ? Infinity : 0;
    } else {
        roiPercentage = ((totalFinancialBenefit - initialInvestment) / initialInvestment) * 100;
    }

    const totalCarbonReduced = annualEmissionReduction * projectLifespan;

    return {
        roiPercentage: roiPercentage,
        paybackPeriod: paybackPeriod,
        npv: npv,
        totalCarbonReduced: totalCarbonReduced,
        totalFinancialBenefit: totalFinancialBenefit
    };
}
