const { currentMonthlyIncome, currentMonthlyRent, currentMonthlyUtilities, currentMonthlyTransportation, currentMonthlyGroceries, newCityCOLIndex, newCityRentIndex, newCityTransportationIndex } = inputs;

// Calculate current total expenses
const currentTotalExpenses = currentMonthlyRent + currentMonthlyUtilities + currentMonthlyTransportation + currentMonthlyGroceries;

// Calculate estimated new specific expenses
const estimatedNewMonthlyRent = currentMonthlyRent * newCityRentIndex;
const estimatedNewMonthlyTransportation = currentMonthlyTransportation * newCityTransportationIndex;

// For Utilities and Groceries, use the general COL index, but apply a floor for realism.
// This ensures that even if the overall index is very low, these essential services don't become unrealistically cheap.
const effectiveCOLIndexForGeneralItems = Math.max(0.7, newCityCOLIndex);

const estimatedNewMonthlyUtilities = currentMonthlyUtilities * effectiveCOLIndexForGeneralItems;
const estimatedNewMonthlyGroceries = currentMonthlyGroceries * effectiveCOLIndexForGeneralItems;

// Calculate estimated total new expenses
const estimatedNewMonthlyExpenses = estimatedNewMonthlyRent + estimatedNewMonthlyUtilities + estimatedNewMonthlyTransportation + estimatedNewMonthlyGroceries;

// Current disposable income (simplified: income - core expenses)
const currentDisposableIncome = currentMonthlyIncome - currentTotalExpenses;

// Required new monthly income to maintain current disposable income
// This is the new total expenses plus the current amount of 'disposable' funds.
const requiredNewMonthlyIncome = estimatedNewMonthlyExpenses + currentDisposableIncome;

// Change in disposable income if new income is *same* as current income
// (Current Income - New Estimated Expenses) - Current Disposable Income
const newDisposableIncomeWithSameSalary = currentMonthlyIncome - estimatedNewMonthlyExpenses;
const disposableIncomeChange = newDisposableIncomeWithSameSalary - currentDisposableIncome;

return {
    estimatedNewMonthlyRent,
    estimatedNewMonthlyExpenses,
    requiredNewMonthlyIncome,
    disposableIncomeChange
};
