const currentSalary = parseFloat(inputs.currentSalary) || 0;
const currentRent = parseFloat(inputs.currentRent) || 0;
const newRent = parseFloat(inputs.newRent) || 0;
const groceriesDiff = (parseFloat(inputs.groceriesIndex) || 0) / 100;
const utilitiesDiff = (parseFloat(inputs.utilitiesIndex) || 0) / 100;

// We need to estimate disposable income. 
// Assumption: 30% tax, 30% rent, 15% groceries, 10% utilities, 15% savings/other
// This is a rough heuristic to weight the 'basket of goods'

const annualRentDiff = (newRent - currentRent) * 12;

// Estimate consumable spending based on salary
// Let's assume 25% of net income goes to variable costs (food, utils, transport)
const netIncome = currentSalary * 0.75; // Approx after tax
const variableSpend = netIncome * 0.30;

// Apply weighted inflation to variable spend
// (50% groceries weight, 25% utilities, 25% other transport/misc)
const avgInflation = (groceriesDiff * 0.5) + (utilitiesDiff * 0.25) + (0 * 0.25);
const variableSpendDiff = variableSpend * avgInflation;

const totalCOLIncrease = annualRentDiff + variableSpendDiff;
const requiredSalary = currentSalary + (totalCOLIncrease / 0.75); // Gross up for taxes

const purchasingPowerChange = ((currentSalary - requiredSalary) / currentSalary) * 100;

return {
    requiredSalary: requiredSalary,
    rentDifference: annualRentDiff,
    purchasingPower: purchasingPowerChange
};
