const protectedAssetValue = Math.max(0, i.protectedAssetValue);
const annualIncidentProbability = Math.max(0, Math.min(100, i.annualIncidentProbability));
const averageCostPerIncident = Math.max(0, i.averageCostPerIncident);
const counterDroneSystemUpfrontCost = Math.max(0, i.counterDroneSystemUpfrontCost);
const annualOMCosts = Math.max(0, i.annualOMCosts);
const systemOperationalLifespan = Math.max(1, i.systemOperationalLifespan); 
const systemEfficacy = Math.max(0, Math.min(100, i.systemEfficacy));
const annualDiscountRate = Math.max(0, i.annualDiscountRate) / 100; 

let annualExpectedLossWithout = 0;
let annualExpectedLossWith = 0;
let annualRiskReduction = 0;
let totalCostOfOwnership = 0;
let netPresentValue = 0;
let returnOnInvestment = 0;
let paybackPeriod = Infinity;

// Step 1: Calculate Annual Expected Loss (AEL)
annualExpectedLossWithout = (annualIncidentProbability / 100) * averageCostPerIncident;
annualExpectedLossWith = annualExpectedLossWithout * (1 - (systemEfficacy / 100));
annualRiskReduction = annualExpectedLossWithout - annualExpectedLossWith; 

// Step 2: Calculate Total Cost of Ownership (TCO) - undiscounted
totalCostOfOwnership = counterDroneSystemUpfrontCost + (annualOMCosts * systemOperationalLifespan);

// Step 3: Calculate Net Present Value (NPV) and Return on Investment (ROI)
let pvBenefitsSum = 0;
let pvOpexSum = 0;

for (let t = 1; t <= systemOperationalLifespan; t++) {
    const discountFactor = Math.pow(1 + annualDiscountRate, t);
    pvBenefitsSum += annualRiskReduction / discountFactor;
    pvOpexSum += annualOMCosts / discountFactor;
}

const totalPVCosts = counterDroneSystemUpfrontCost + pvOpexSum;
netPresentValue = pvBenefitsSum - totalPVCosts;

if (totalPVCosts > 0) {
    returnOnInvestment = (netPresentValue / totalPVCosts) * 100;
} else if (netPresentValue > 0) {
    returnOnInvestment = Infinity;
} else {
    returnOnInvestment = 0; 
}

// Step 4: Calculate Payback Period (Undiscounted)
const annualNetBenefit = annualRiskReduction - annualOMCosts;
if (annualNetBenefit > 0) {
    paybackPeriod = counterDroneSystemUpfrontCost / annualNetBenefit;
    if (paybackPeriod <= 0 && counterDroneSystemUpfrontCost === 0) {
        paybackPeriod = 0;
    } else if (paybackPeriod > systemOperationalLifespan) {
        paybackPeriod = Infinity;
    }
} else if (counterDroneSystemUpfrontCost === 0 && annualNetBenefit >= 0) {
    paybackPeriod = 0;
} else {
    paybackPeriod = Infinity;
}

// Final adjustments for edge cases where specific inputs might lead to undefined outputs
if (systemOperationalLifespan === 0) { // Although Math.max(1, lifespan) should handle this, defensive coding
    annualExpectedLossWithout = 0;
    annualExpectedLossWith = 0;
    annualRiskReduction = 0;
    totalCostOfOwnership = counterDroneSystemUpfrontCost;
    netPresentValue = -counterDroneSystemUpfrontCost;
    returnOnInvestment = (counterDroneSystemUpfrontCost === 0) ? 0 : -100; 
    paybackPeriod = (counterDroneSystemUpfrontCost === 0) ? 0 : Infinity;
}

return {
    annualExpectedLossWithout: annualExpectedLossWithout,
    annualExpectedLossWith: annualExpectedLossWith,
    annualRiskReduction: annualRiskReduction,
    totalCostOfOwnership: totalCostOfOwnership,
    netPresentValue: netPresentValue,
    returnOnInvestment: returnOnInvestment,
    paybackPeriod: paybackPeriod,
};
