function calculateAvalancheRisk(inputs) {
  const { slopeAngle, slopeAspect, elevation, snowpackDepth, recentSnowfall, historicalFrequency, stabilityIndex, assetValue, assetFragility, disruptionFactor } = inputs;

  // Input validation and clamping
  const safeSlopeAngle = Math.min(Math.max(slopeAngle, 15), 60); // Avalanches typically > 25, rarely > 60
  const safeElevation = Math.min(Math.max(elevation, 0), 6000);
  const safeSnowpackDepth = Math.min(Math.max(snowpackDepth, 0), 1000);
  const safeRecentSnowfall = Math.min(Math.max(recentSnowfall, 0), 200);
  const safeHistoricalFrequency = Math.min(Math.max(historicalFrequency, 0), 10);
  const safeStabilityIndex = Math.min(Math.max(stabilityIndex, 1), 5);
  const safeAssetValue = Math.max(assetValue, 0);
  const safeAssetFragility = Math.min(Math.max(assetFragility, 0), 1);
  const safeDisruptionFactor = Math.min(Math.max(disruptionFactor, 1), 5);

  // Step 1: Calculate Avalanche Hazard Score (AHS)
  let ahs = 0;

  // Slope Angle contribution (main driver): 25-35 good, 35-45 critical, >45 very critical
  if (safeSlopeAngle < 25) ahs += (safeSlopeAngle - 15) * 0.5; // Minor increase
  else if (safeSlopeAngle >= 25 && safeSlopeAngle <= 35) ahs += (safeSlopeAngle - 25) * 1.5 + 5;
  else if (safeSlopeAngle > 35 && safeSlopeAngle <= 45) ahs += (safeSlopeAngle - 35) * 2.5 + 20;
  else ahs += (safeSlopeAngle - 45) * 3.5 + 45; // Max 60 -> 45 + 15*3.5 = 97.5

  // Aspect contribution (modifiers)
  const lowerAspect = slopeAspect.toLowerCase();
  if (lowerAspect.includes('north') || lowerAspect.includes('east')) ahs *= 1.15; // More prone to wind loading, persistent weak layers
  else if (lowerAspect.includes('west')) ahs *= 1.05;
  else if (lowerAspect.includes('south')) ahs *= 0.9; // More sun exposure, faster stabilization

  // Elevation contribution (higher = more snow/exposure)
  ahs += (safeElevation / 6000) * 10;

  // Snowpack Depth contribution (more snow = more load)
  ahs += (safeSnowpackDepth / 1000) * 15;

  // Recent Snowfall contribution (instability factor)
  ahs += (safeRecentSnowfall / 200) * 25;

  // Historical Frequency contribution (recurrence interval)
  ahs += safeHistoricalFrequency * 3;

  // Normalize AHS to be a probability-like score / 100, then adjust for stability
  ahs = Math.min(Math.max(ahs, 0), 120); // Cap AHS before stability adjustment

  // Stability Index is a strong modifier
  // 1 = Very Poor (x2.0), 2 = Poor (x1.5), 3 = Moderate (x1.0), 4 = Good (x0.7), 5 = Excellent (x0.4)
  let stabilityMultiplier;
  if (safeStabilityIndex <= 1.5) stabilityMultiplier = 2.0;
  else if (safeStabilityIndex <= 2.5) stabilityMultiplier = 1.5;
  else if (safeStabilityIndex <= 3.5) stabilityMultiplier = 1.0;
  else if (safeStabilityIndex <= 4.5) stabilityMultiplier = 0.7;
  else stabilityMultiplier = 0.4;

  let avalancheProbability = (ahs * stabilityMultiplier) / 1.2; // Adjust for scale and multiplier factor
  avalancheProbability = Math.min(Math.max(avalancheProbability, 0), 100); // Final cap between 0-100

  // If slope is too shallow, probability is near zero unless stability is extremely poor
  if (safeSlopeAngle < 20) avalancheProbability *= 0.1;
  if (safeSlopeAngle < 15) avalancheProbability = 0.01; // Effectively zero

  // Step 2: Calculate Consequence Score (CS)
  // Base consequence is asset value modified by fragility
  let consequenceBase = safeAssetValue * safeAssetFragility;

  // Apply economic disruption factor for indirect costs
  let potentialDamageCost = consequenceBase * safeDisruptionFactor;

  // Consequence Score (normalize for display) based on potential damage cost relative to a max assumed damage (e.g., 100M USD)
  // Max theoretical damage could be 100M * 1 * 5 = 500M. Let's cap CS at 100.
  let consequenceScore = (potentialDamageCost / 500000000) * 100;
  consequenceScore = Math.min(Math.max(consequenceScore, 0), 100); // Cap between 0-100

  // Step 3: Calculate Total Risk Score (TRS)
  // Risk = Probability * Consequence
  // Normalize total risk to a scale out of 10000 (100 * 100)
  let totalRiskScore = (avalancheProbability / 100) * (consequenceScore / 100) * 10000;
  totalRiskScore = Math.min(Math.max(totalRiskScore, 0), 10000);

  return {
    avalancheProbability: avalancheProbability,
    consequenceScore: consequenceScore,
    totalRiskScore: totalRiskScore,
    potentialDamageCost: Math.round(potentialDamageCost)
  };
}
