function calculate() {
    const design_strength_factor = parseFloat(inputs.design_strength_factor);
    const maintenance_frequency_score = parseFloat(inputs.maintenance_frequency_score);
    const emergency_plan_score = parseFloat(inputs.emergency_plan_score);
    const redundancy_level = parseFloat(inputs.redundancy_level);
    const backup_power_autonomy_hours = parseFloat(inputs.backup_power_autonomy_hours);
    const staff_training_frequency_score = parseFloat(inputs.staff_training_frequency_score);
    const infrastructure_age_years = parseFloat(inputs.infrastructure_age_years);

    let base_score = 0;

    // 1. Calculate Core Resilience Score (Max 80 points)
    // Each score (1-5) is normalized to a 0-4 range and then scaled.
    // Design: max 20 points
    base_score += ((design_strength_factor - 1) / 4) * 20;
    // Maintenance: max 15 points
    base_score += ((maintenance_frequency_score - 1) / 4) * 15;
    // Emergency Plan: max 20 points
    base_score += ((emergency_plan_score - 1) / 4) * 20;
    // Redundancy: max 15 points
    base_score += ((redundancy_level - 1) / 4) * 15;
    // Staff Training: max 10 points
    base_score += ((staff_training_frequency_score - 1) / 4) * 10;

    // Ensure base_score doesn't exceed its theoretical maximum for these inputs.
    base_score = Math.min(base_score, 80);

    // 2. Apply Age Deduction (Max 10 points deduction)
    let age_deduction = 0;
    if (infrastructure_age_years > 10) {
        age_deduction = Math.max(0, (infrastructure_age_years - 10) * 0.25);
    }
    age_deduction = Math.min(age_deduction, 10); // Cap deduction at 10 points
    base_score -= age_deduction;

    // 3. Apply Backup Power Bonus (Max 10 points bonus)
    let backup_bonus = 0;
    if (backup_power_autonomy_hours >= 336) { // 2 weeks
        backup_bonus = 10;
    } else if (backup_power_autonomy_hours >= 168) { // 1 week
        backup_bonus = 8;
    } else if (backup_power_autonomy_hours >= 72) { // 3 days
        backup_bonus = 5;
    } else if (backup_power_autonomy_hours >= 24) { // 1 day
        backup_bonus = 2;
    }
    base_score += backup_bonus;

    // 4. Final Score Normalization and Clamping
    let resilience_score = Math.max(0, Math.min(100, base_score));

    // 5. Determine Risk Level
    let risk_level;
    if (resilience_score >= 80) {
        risk_level = "Low";
    } else if (resilience_score >= 60) {
        risk_level = "Moderate";
    } else if (resilience_score >= 40) {
        risk_level = "High";
    } else {
        risk_level = "Critical";
    }

    return {
        resilience_score: resilience_score,
        risk_level: risk_level
    };
}
