const supplierDiversityScore = parseFloat(inputs.supplierDiversityScore);
const geopoliticalStabilityScore = parseFloat(inputs.geopoliticalStabilityScore);
const inventoryBufferMonths = parseFloat(inputs.inventoryBufferMonths);
const alternativeMaterialAvailabilityScore = parseFloat(inputs.alternativeMaterialAvailabilityScore);
const singlePointFailureRisk = parseFloat(inputs.singlePointFailureRisk);
const logisticsNetworkRedundancy = parseFloat(inputs.logisticsNetworkRedundancy);
const demandVolatilityImpact = parseFloat(inputs.demandVolatilityImpact);
const regulatoryEnvironmentStability = parseFloat(inputs.regulatoryEnvironmentStability);

// Input validation and clamping (should largely be handled by UI min/max, but for robustness)
const getClamped = (val, min, max) => Math.max(min, Math.min(max, val));

const diversity = getClamped(supplierDiversityScore, 0, 10);
const stability = getClamped(geopoliticalStabilityScore, 0, 10);
const inventoryNormalized = getClamped((inventoryBufferMonths / 24) * 10, 0, 10); // Normalize 0-24 months to 0-10 score
const altMaterial = getClamped(alternativeMaterialAvailabilityScore, 0, 10);
const singlePointResilience = getClamped(10 - singlePointFailureRisk, 0, 10); // Invert risk score for resilience
const logistics = getClamped(logisticsNetworkRedundancy, 0, 10);
const demandResilience = getClamped(10 - demandVolatilityImpact, 0, 10); // Invert impact score for resilience
const regulatory = getClamped(regulatoryEnvironmentStability, 0, 10);

// Define weights for each factor (sum should be 1.0)
const weights = {
  diversity: 0.25,
  stability: 0.20,
  inventory: 0.15,
  altMaterial: 0.15,
  singlePoint: 0.10,
  logistics: 0.05,
  demand: 0.05,
  regulatory: 0.05
};

// Calculate Resilience Index (weighted average)
let resilienceIndex = 
  (diversity * weights.diversity) +
  (stability * weights.stability) +
  (inventoryNormalized * weights.inventory) +
  (altMaterial * weights.altMaterial) +
  (singlePointResilience * weights.singlePoint) +
  (logistics * weights.logistics) +
  (demandResilience * weights.demand) +
  (regulatory * weights.regulatory);

// Ensure index is within 0-10 range due to potential floating point errors or edge cases
resilienceIndex = getClamped(resilienceIndex, 0, 10);

// Calculate Risk Mitigation Potential (how much 'room for improvement' there is)
// If index is 10, potential is 0. If index is 0, potential is 100.
const riskMitigationPotential = ((10 - resilienceIndex) / 10) * 100;

return {
  resilienceIndex: resilienceIndex,
  riskMitigationPotential: riskMitigationPotential
};
