const numJurisdictionsCap = Math.min(15, inputs.numJurisdictions);
const jurisdictionRiskComponent = numJurisdictionsCap / 15; // Normalized 0-1

let dataSensitivityImpact;
switch (inputs.dataSensitivityLevel) {
  case 1: dataSensitivityImpact = 0.2; break; // Low
  case 2: dataSensitivityImpact = 0.4; break; // Medium
  case 3: dataSensitivityImpact = 0.7; break; // High
  case 4: dataSensitivityImpact = 1.0; break; // Critical
  default: dataSensitivityImpact = 0.2; // Default to low if invalid
}

const privacyFrameworkRiskComponent = (100 - inputs.privacyFrameworkMaturity) / 100; // 0=good, 1=bad
const dataResidencyRiskComponent = (100 - inputs.dataResidencyComplianceScore) / 100; // 0=good, 1=bad
const transferMechanismRiskComponent = (100 - inputs.transferMechanismStrength) / 100; // 0=good, 1=bad
const highRiskJurisdictionExposureComponent = inputs.highRiskJurisdictionExposure / 100; // Normalized 0-1
const incidentResponseRiskComponent = (100 - inputs.incidentResponseMaturity) / 100; // 0=good, 1=bad

// Apply weights to each component
const weightedRisk = (
  (jurisdictionRiskComponent * 0.15) +
  (dataSensitivityImpact * 0.25) +
  (privacyFrameworkRiskComponent * 0.20) +
  (dataResidencyRiskComponent * 0.10) +
  (transferMechanismRiskComponent * 0.15) +
  (highRiskJurisdictionExposureComponent * 0.10) +
  (incidentResponseRiskComponent * 0.05)
);

let complianceRiskScore = Math.round(weightedRisk * 100);

// Ensure a minimum score to reflect inherent risk of cross-border transfers
complianceRiskScore = Math.max(5, complianceRiskScore);

// Cap the score at 100
complianceRiskScore = Math.min(100, complianceRiskScore);

let riskCategory;
if (complianceRiskScore <= 30) {
  riskCategory = 'Low Risk';
} else if (complianceRiskScore <= 60) {
  riskCategory = 'Medium Risk';
} else if (complianceRiskScore <= 85) {
  riskCategory = 'High Risk';
} else {
  riskCategory = 'Critical Risk';
}

return { complianceRiskScore, riskCategory };
