function calculateTariffImpact(inputs) {
    const importValue = parseFloat(inputs.importValue) || 0;
    const exportValue = parseFloat(inputs.exportValue) || 0;
    const proposedTariffRate = parseFloat(inputs.proposedTariffRate) || 0;
    const existingTariffRate = parseFloat(inputs.existingTariffRate) || 0;
    const tariffIncidence = parseFloat(inputs.tariffIncidence) || 0;
    const supplyChainElasticity = parseFloat(inputs.supplyChainElasticity) || 0;
    const retaliatoryTariffProbability = parseFloat(inputs.retaliatoryTariffProbability) || 0;
    const retaliatoryTariffRate = parseFloat(inputs.retaliatoryTariffRate) || 0;
    const consumerPriceElasticity = parseFloat(inputs.consumerPriceElasticity) || 0;

    // Ensure percentages are treated as decimals and capped within 0-100%
    const proposedTariffRate_dec = Math.min(Math.max(proposedTariffRate, 0), 100) / 100;
    const existingTariffRate_dec = Math.min(Math.max(existingTariffRate, 0), 100) / 100;
    const tariffIncidence_dec = Math.min(Math.max(tariffIncidence, 0), 100) / 100;
    const supplyChainElasticity_dec = Math.min(Math.max(supplyChainElasticity, 0), 100) / 100;
    const retaliatoryTariffProbability_dec = Math.min(Math.max(retaliatoryTariffProbability, 0), 100) / 100;
    const retaliatoryTariffRate_dec = Math.min(Math.max(retaliatoryTariffRate, 0), 100) / 100;

    // --- Calculations ---

    // 1. Net Import Cost Change for Businesses:
    // The direct financial change (increase or decrease) for businesses importing goods due to tariff adjustments.
    const tariffRateChange_dec = proposedTariffRate_dec - existingTariffRate_dec;
    const netImportCostChange = importValue * tariffRateChange_dec;

    // 2. Government Tariff Revenue:
    // Total revenue generated by the *proposed* tariff rate for the imposing government.
    const tariffRevenueGenerated = importValue * proposedTariffRate_dec;

    // 3. Consumer Price Increase Impact:
    // The portion of the positive net import cost change that is passed on to consumers.
    // Only an 'increase' is considered for consumer impact, not savings.
    const consumerPriceIncreaseImpact = Math.max(0, netImportCostChange) * tariffIncidence_dec;

    // 4. Potential Export Revenue Loss from Retaliation:
    // Estimates the reduction in export revenue due to retaliatory tariffs and subsequent reduced demand.
    let potentialExportRevenueLoss = 0;
    if (retaliatoryTariffProbability_dec > 0 && exportValue > 0) {
        // Portion of retaliatory tariff cost that the exporter's supply chain *cannot* absorb.
        const effectiveRetaliatoryTariffRate_dec = retaliatoryTariffRate_dec * (1 - supplyChainElasticity_dec);

        // Reduction in demand/volume for exports due to the effective price increase (based on elasticity).
        const demandReductionFactor = consumerPriceElasticity * effectiveRetaliatoryTariffRate_dec;

        // Total potential revenue loss, scaled by the probability of retaliation.
        potentialExportRevenueLoss = exportValue * demandReductionFactor * retaliatoryTariffProbability_dec;
    }
    // Ensure potential revenue loss is non-negative.
    potentialExportRevenueLoss = Math.max(0, potentialExportRevenueLoss);

    // 5. Overall Economic Impact:
    // Aggregates the net financial burden on the economy, encompassing direct import cost changes (which distribute between
    // businesses and consumers) and potential export revenue losses. Government revenue is a transfer, not a net burden.
    const overallEconomicImpact = netImportCostChange + potentialExportRevenueLoss;

    return {
        netImportCostChange: netImportCostChange,
        tariffRevenueGenerated: tariffRevenueGenerated,
        consumerPriceIncreaseImpact: consumerPriceIncreaseImpact,
        potentialExportRevenueLoss: potentialExportRevenueLoss,
        overallEconomicImpact: overallEconomicImpact,
    };
}
