function calculate({ value_of_goods_usd, original_shipping_cost_usd, disruption_duration_days, new_tariff_rate_increase_percent, logistics_delay_additional_days, daily_detention_demurrage_cost_usd, alternative_route_cost_increase_percent, lost_sales_profit_margin_percent, supply_chain_reconfiguration_cost_usd }) {

  // Ensure all inputs are treated as numbers and handle potential non-numeric inputs gracefully
  const valGoods = Math.max(0, Number(value_of_goods_usd) || 0);
  const origShipping = Math.max(0, Number(original_shipping_cost_usd) || 0);
  const disruptionDays = Math.max(0, Number(disruption_duration_days) || 0);
  const newTariffRate = Math.max(0, Number(new_tariff_rate_increase_percent) || 0);
  const delayDays = Math.max(0, Number(logistics_delay_additional_days) || 0);
  const dailyDetention = Math.max(0, Number(daily_detention_demurrage_cost_usd) || 0);
  const altRouteIncrease = Math.max(0, Number(alternative_route_cost_increase_percent) || 0);
  const lostSalesMargin = Math.max(0, Number(lost_sales_profit_margin_percent) || 0);
  const reconfigCost = Math.max(0, Number(supply_chain_reconfiguration_cost_usd) || 0);

  // 1. Calculate Increased Tariff Cost
  const tariffIncreaseCost = valGoods * (newTariffRate / 100);

  // 2. Calculate Storage/Detention Cost (assuming detention applies for the duration of the *additional* delay)
  const storageDetentionCost = dailyDetention * delayDays;

  // 3. Calculate Alternative Logistics Cost
  // This assumes the original shipping cost is base for calculating increase, if an alternative route is necessitated
  const alternativeLogisticsCost = origShipping * (altRouteIncrease / 100);

  // 4. Calculate Total Direct Disruption Cost
  const totalDirectDisruptionCost = tariffIncreaseCost + storageDetentionCost + alternativeLogisticsCost + reconfigCost;

  // 5. Calculate Lost Sales/Opportunity Cost
  // This represents the profit margin lost on goods that are delayed or potentially spoiled/unsold due to disruption
  const lostSalesOpportunityCost = valGoods * (lostSalesMargin / 100);

  // 6. Calculate Total Indirect Disruption Cost
  // For this calculator, we're focusing primarily on quantifiable financial loss. Reputation, market share etc. are harder to directly quantify here.
  const totalIndirectDisruptionCost = lostSalesOpportunityCost; // Currently only lost sales considered indirect here

  // 7. Calculate Grand Total Estimated Disruption Cost
  const grandTotalDisruptionCost = totalDirectDisruptionCost + totalIndirectDisruptionCost;

  return {
    tariff_increase_cost: tariffIncreaseCost,
    storage_detention_cost: storageDetentionCost,
    alternative_logistics_cost: alternativeLogisticsCost,
    total_direct_disruption_cost: totalDirectDisruptionCost,
    lost_sales_opportunity_cost: lostSalesOpportunityCost,
    total_indirect_disruption_cost: totalIndirectDisruptionCost,
    grand_total_disruption_cost: grandTotalDisruptionCost
  };
}
