const emissionsBaseline = inputs.emissionsBaseline; // MtCO2e
const coordinationLevel = inputs.coordinationLevel / 100; // 0-1
const reductionTarget = inputs.reductionTarget / 100; // 0-1
const techAdoptionRate = inputs.techAdoptionRate / 100; // 0-1
const econGrowthSensitivity = inputs.econGrowthSensitivity; // 0-1
const tradeAgreementCoverage = inputs.tradeAgreementCoverage / 100; // 0-1

// Input validation and edge case handling
if (emissionsBaseline <= 0) {
    return {
        projectedEmissions: 0,
        tempIncrease: 0,
        economicImpact: 0,
        disasterRiskIndex: 0
    };
}

// --- Step 1: Calculate Effective Emissions Reduction Factor ---
// Coordination amplifies the target, and technology adoption improves efficiency.
// Base factor is reduction target, then adjusted.
let effectiveReductionFactor = reductionTarget * (0.6 + coordinationLevel * 0.4) * (0.7 + techAdoptionRate * 0.3);
effectiveReductionFactor = Math.min(effectiveReductionFactor, 0.95); // Cap at 95% reduction

// --- Step 2: Calculate Projected Global Emissions by 2050 ---
const projectedEmissionsRaw = emissionsBaseline * (1 - effectiveReductionFactor);
const minAbsoluteEmissions = emissionsBaseline * 0.05; // Assumes a floor of 5% of baseline emissions
const finalProjectedEmissions = Math.max(projectedEmissionsRaw, minAbsoluteEmissions);

// --- Step 3: Calculate Projected Global Temperature Increase by 2050 ---
// A simplified linear model: baseline 3.5°C if no reduction, 1.5°C for max reduction.
const emissionsRatio = finalProjectedEmissions / emissionsBaseline;
let tempIncrease = 1.5 + (emissionsRatio * 2.0); // Ranges from 1.5 + 0.05*2 = 1.6 to 1.5 + 1*2 = 3.5
tempIncrease = Math.min(Math.max(tempIncrease, 1.5), 3.5); // Cap between 1.5 and 3.5°C for 2050

// --- Step 4: Estimate Global Economic Impact by 2050 (Trillion USD) ---
// Components: Costs of transition, costs of climate damage, benefits of green economy.
const maxClimateDamageCost = 35; // Trillion USD if no action
const maxTransitionCost = 25; // Trillion USD if aggressive, uncoordinated reduction
const maxGreenEconomyBenefit = 20; // Trillion USD from innovation, new markets, coordinated efforts

// Cost related to emissions reduction (transition costs)
let reductionCost = reductionTarget * maxTransitionCost * (1 + econGrowthSensitivity * 0.7);
reductionCost = reductionCost * (1 - coordinationLevel * 0.4 - techAdoptionRate * 0.3); // Coordination/tech reduce direct costs

// Cost related to climate change impacts (damages from residual emissions)
let climateDamageCost = emissionsRatio * maxClimateDamageCost * (1 - coordinationLevel * 0.2); // Some mitigation via adaptation in coordinated efforts

// Benefits from green transition, innovation, and supportive trade agreements
let greenBenefit = (coordinationLevel * 0.4 + techAdoptionRate * 0.3 + tradeAgreementCoverage * 0.3) * maxGreenEconomyBenefit;
greenBenefit = greenBenefit * (0.7 + reductionTarget * 0.3); // Higher target incentivizes more green benefit

const economicImpact = greenBenefit - reductionCost - climateDamageCost;
const finalEconomicImpact = Math.min(Math.max(economicImpact, -50), 25); // Cap between -50 and 25 Trillion USD

// --- Step 5: Calculate Climate Disaster Risk Index (0-10) ---
// Risk is primarily driven by emissions and temperature, mitigated by coordination and tech.
let disasterRiskIndex = emissionsRatio * 10; // Base risk proportional to emissions
disasterRiskIndex = disasterRiskIndex * (1 - coordinationLevel * 0.4); // Coordination significantly reduces risk
disasterRiskIndex = disasterRiskIndex * (1 - techAdoptionRate * 0.2); // Tech helps mitigate and build resilience

// Further adjustment based on projected temperature increase
disasterRiskIndex = disasterRiskIndex * (tempIncrease / 2.5); // Scale by temperature relative to a 'moderate' 2.5C scenario

const finalDisasterRiskIndex = Math.min(Math.max(disasterRiskIndex, 0.5), 10); // Cap between 0.5 and 10


return {
    projectedEmissions: Math.round(finalProjectedEmissions),
    tempIncrease: parseFloat(tempIncrease.toFixed(2)),
    economicImpact: parseFloat(finalEconomicImpact.toFixed(2)),
    disasterRiskIndex: parseFloat(finalDisasterRiskIndex.toFixed(1))
};
