const gender = inputs.gender.toLowerCase();
const age = parseFloat(inputs.age);
const weight_kg = parseFloat(inputs.weight);
const height_cm = parseFloat(inputs.height);
const activityLevel = inputs.activityLevel.toLowerCase();

let bmr;
// Mifflin-St Jeor Equation for BMR
if (gender === 'male') {
    bmr = (10 * weight_kg) + (6.25 * height_cm) - (5 * age) + 5;
} else if (gender === 'female') {
    bmr = (10 * weight_kg) + (6.25 * height_cm) - (5 * age) - 161;
} else {
    throw new Error('Invalid gender. Please enter "male" or "female".');
}

// Activity Level Factors (for non-exercise activity thermogenesis and thermic effect of food)
// These factors are applied to BMR to estimate general daily expenditure excluding explicit exercises.
const activityFactors = {
    'sedentary': 1.2, // little or no exercise
    'lightly active': 1.375, // light exercise/sports 1-3 days/week
    'moderately active': 1.55, // moderate exercise/sports 3-5 days/week
    'very active': 1.725, // hard exercise/sports 6-7 days a week
    'super active': 1.9 // very hard exercise/physical job
};

const activityFactor = activityFactors[activityLevel] || 1.2; // Default to sedentary if invalid input

// Calories burned from specific activities
// Formula: Calories = METs * Weight (kg) * Duration (hours)
// 1 MET = 1 kcal/kg/hour

const activity1_duration_min = parseFloat(inputs.activity1_duration) || 0;
const activity1_met = parseFloat(inputs.activity1_met) || 0;
const activity2_duration_min = parseFloat(inputs.activity2_duration) || 0;
const activity2_met = parseFloat(inputs.activity2_met) || 0;

const calories_activity1 = activity1_met * weight_kg * (activity1_duration_min / 60);
const calories_activity2 = activity2_met * weight_kg * (activity2_duration_min / 60);

const total_explicit_activity_calories = calories_activity1 + calories_activity2;

// Base daily energy expenditure from BMR and general activity (excluding specific exercises added)
const base_daily_expenditure_without_explicit_exercise = bmr * activityFactor;

// Total Daily Energy Expenditure (TDEE) = (BMR * General Activity Factor) + Explicit Exercise Calories
const total_daily_expenditure = base_daily_expenditure_without_explicit_exercise + total_explicit_activity_calories;

return {
    bmr: Math.round(bmr),
    activity_calories: Math.round(total_explicit_activity_calories),
    total_daily_expenditure: Math.round(total_daily_expenditure)
};
