/**
 * Calculates the annual carbon footprint based on user inputs.
 * Emission factors are simplified averages for illustrative purposes.
 * All outputs are in metric tons of CO2e per year.
 */

function calculateCarbonFootprint(inputs) {
  const householdSize = inputs.householdSize || 1;
  const electricityKwhMonth = inputs.electricityKwhMonth || 0;
  const naturalGasThermsMonth = inputs.naturalGasThermsMonth || 0;
  const heatingOilGallonsYear = inputs.heatingOilGallonsYear || 0;
  const milesDrivenWeekly = inputs.milesDrivenWeekly || 0;
  const vehicleFuelEfficiency = inputs.vehicleFuelEfficiency || 1;
  const dietType = inputs.dietType || 'average_meat';
  const flightsHoursYear = inputs.flightsHoursYear || 0;
  const shoppingFrequency = inputs.shoppingFrequency || 'occasionally';

  let totalCO2e = 0;

  // Emission Factors (approximations, sourced from various environmental agencies/studies for general context):
  const EF_ELECTRICITY_KWH = 0.42; // kg CO2e/kWh (US average, varies by grid)
  const EF_NATURAL_GAS_THERM = 5.3; // kg CO2e/therm
  const EF_HEATING_OIL_GALLON = 10.1; // kg CO2e/gallon
  const EF_GASOLINE_GALLON = 8.87; // kg CO2e/gallon

  // Diet-specific annual CO2e per person (kg CO2e/year, illustrative averages)
  const EF_DIET = {
    'vegan': 1500, // Very low impact
    'vegetarian': 1900,
    'pescatarian': 2200,
    'average_meat': 2500,
    'heavy_meat': 3000 // High impact, multiple meat meals daily
  };

  // Flight emission factor (kg CO2e/hour, simplified for commercial short/medium haul)
  const EF_FLIGHT_HOUR = 150; // Approximates for economy class, includes non-CO2 effects with multiplier

  // Consumption/Shopping emission factors (kg CO2e/year per person, very rough estimates)
  const EF_SHOPPING = {
    'rarely': 500,
    'occasionally': 1000,
    'frequently': 1500,
    'very_frequently': 2000
  };

  // 1. Home Energy Emissions
  const electricityAnnualCO2e = (electricityKwhMonth * 12 * EF_ELECTRICITY_KWH);
  const naturalGasAnnualCO2e = (naturalGasThermsMonth * 12 * EF_NATURAL_GAS_THERM);
  const heatingOilAnnualCO2e = (heatingOilGallonsYear * EF_HEATING_OIL_GALLON);
  totalCO2e += (electricityAnnualCO2e + naturalGasAnnualCO2e + heatingOilAnnualCO2e);

  // 2. Transportation Emissions
  // Avoid division by zero for vehicleFuelEfficiency
  const effectiveFuelEfficiency = vehicleFuelEfficiency > 0 ? vehicleFuelEfficiency : 1;
  const annualGallonsUsed = (milesDrivenWeekly * 52) / effectiveFuelEfficiency;
  const drivingAnnualCO2e = (annualGallonsUsed * EF_GASOLINE_GALLON);
  totalCO2e += drivingAnnualCO2e;

  // 3. Diet Emissions (per household)
  const dietAnnualCO2e = (EF_DIET[dietType] || EF_DIET['average_meat']) * householdSize;
  totalCO2e += dietAnnualCO2e;

  // 4. Flight Emissions (per household member, then aggregated)
  const flightsAnnualCO2e = (flightsHoursYear * EF_FLIGHT_HOUR) * householdSize;
  totalCO2e += flightsAnnualCO2e;

  // 5. Consumption/Shopping Emissions (per household member, then aggregated)
  const shoppingAnnualCO2e = (EF_SHOPPING[shoppingFrequency] || EF_SHOPPING['occasionally']) * householdSize;
  totalCO2e += shoppingAnnualCO2e;

  // Convert total kg CO2e to metric tons CO2e
  const totalAnnualCO2e = totalCO2e / 1000;

  return {
    totalAnnualCO2e: totalAnnualCO2e
  };
}
