const numServers = parseFloat(inputs.numServers);
const avgServerPower = parseFloat(inputs.avgServerPower);
const networkDevicePower = parseFloat(inputs.networkDevicePower);
const otherEquipmentPower = parseFloat(inputs.otherEquipmentPower);
const lightingPowerDensity = parseFloat(inputs.lightingPowerDensity);
const roomArea = parseFloat(inputs.roomArea);
let pue = parseFloat(inputs.pue);

// --- Input Validations and Defaults ---
if (isNaN(numServers) || numServers < 0) return { error: "Number of servers must be a non-negative number." };
if (isNaN(avgServerPower) || avgServerPower < 0) return { error: "Average server power must be a non-negative number." };
if (isNaN(networkDevicePower) || networkDevicePower < 0) return { error: "Network device power must be a non-negative number." };
if (isNaN(otherEquipmentPower) || otherEquipmentPower < 0) return { error: "Other IT equipment power must be a non-negative number." };
if (isNaN(lightingPowerDensity) || lightingPowerDensity < 0) return { error: "Lighting power density must be a non-negative number." };
if (isNaN(roomArea) || roomArea <= 0) return { error: "Room area must be a positive number." };
if (isNaN(pue) || pue < 1.0) {
    pue = 1.0; // PUE cannot be less than 1.0, default to 1.0 if invalid or too low
}

// --- Calculations ---

// 1. Calculate Total IT Equipment Power (Watts)
const itPowerWatts = (numServers * avgServerPower) + networkDevicePower + otherEquipmentPower;

// 2. Calculate Heat Gain from Lighting (Watts)
const lightingHeatGainWatts = lightingPowerDensity * roomArea;

// 3. Estimate Miscellaneous Non-IT Heat Load (e.g., security cameras, small appliances, etc.)
// Let's use a percentage of the IT load as a simple estimation.
const miscNonITHardwareHeatWatts = itPowerWatts * 0.05; // 5% of IT load for other non-IT hardware heat

// 4. Total Heat Load (Watts) before cooling contingency
const totalHeatLoadBeforeContingencyWatts = itPowerWatts + lightingHeatGainWatts + miscNonITHardwareHeatWatts;

// 5. Apply a contingency factor for cooling capacity (e.g., 20% buffer for future growth, peak loads, and inefficiencies)
const coolingContingencyFactor = 1.20; // 20% buffer
const requiredCoolingWatts = totalHeatLoadBeforeContingencyWatts * coolingContingencyFactor;

// --- Convert to desired units ---

// Total IT Load in kW
const totalITLoadKw = itPowerWatts / 1000;

// Required Cooling Capacity in kW
const totalCoolingKw = requiredCoolingWatts / 1000;

// Required Cooling Capacity in BTU/hr (1 kW = 3412.14 BTU/hr)
const totalCoolingBTU = totalCoolingKw * 3412.14;

// Total Data Center Facility Power Consumption (kW)
// PUE (Power Usage Effectiveness) relates total facility power to IT equipment power.
// PUE = Total Facility Power / IT Equipment Power
// So, Total Facility Power = IT Equipment Power * PUE
const totalFacilityPowerKw = totalITLoadKw * pue;

// --- Return Results ---
return {
    totalITLoadKw: totalITLoadKw,
    totalCoolingKw: totalCoolingKw,
    totalCoolingBTU: totalCoolingBTU,
    totalFacilityPowerKw: totalFacilityPowerKw
};
