function calculate(inputs) {
  const clamp = (num, min, max) => Math.min(Math.max(num, min), max);

  // Clamp inputs to their valid ranges
  const politicalStability = clamp(inputs.politicalStability, 1, 10);
  const energyReliability = clamp(inputs.energyReliability, 0, 100);
  const internetResilience = clamp(inputs.internetResilience, 1, 10);
  const climateHazard = clamp(inputs.climateHazard, 1, 10);
  const regulatoryComplexity = clamp(inputs.regulatoryComplexity, 1, 10);
  const waterStress = clamp(inputs.waterStress, 1, 10);
  const supplyChainVulnerability = clamp(inputs.supplyChainVulnerability, 1, 10);

  // Step 1: Normalize inputs to a common 'risk factor' scale (1-10, where higher is worse)
  // Political Stability: 10 (high risk) to 1 (low risk)
  const politicalRiskFactor = 11 - politicalStability; 
  // Energy Reliability: 10 (high risk) to 0 (low risk). (100-0)/10 = 10. (100-100)/10 = 0.
  const energyRiskFactor = (100 - energyReliability) / 10; 
  // Internet Resilience: 10 (high risk) to 1 (low risk)
  const internetRiskFactor = 11 - internetResilience;
  // Climate Hazard: already 1 (low) to 10 (extreme)
  const climateRiskFactor = climateHazard;
  // Regulatory Complexity: already 1 (simple) to 10 (burdensome)
  const regulatoryRiskFactor = regulatoryComplexity;
  // Water Stress: already 1 (low) to 10 (high)
  const waterRiskFactor = waterStress;
  // Supply Chain Vulnerability: already 1 (low) to 10 (high)
  const supplyChainRiskFactor = supplyChainVulnerability;

  // Step 2: Apply weights to each risk factor
  const wPolitical = 0.25; 
  const wEnergy = 0.20;
  const wInternet = 0.15;
  const wClimate = 0.12;
  const wRegulatory = 0.10;
  const wWater = 0.08;
  const wSupplyChain = 0.10;

  // Check if weights sum to 1, or adjust for minor floating point errors
  const totalWeights = wPolitical + wEnergy + wInternet + wClimate + wRegulatory + wWater + wSupplyChain;
  if (Math.abs(totalWeights - 1.0) > 0.001) {
    // console.warn("Weights do not sum to 1. Adjusting proportionally.");
    // For this context, assuming weights are precisely defined, but robust code would normalize if they don't.
  }

  // Step 3: Calculate the weighted sum of risk factors
  let weightedSum = 
    (politicalRiskFactor * wPolitical) +
    (energyRiskFactor * wEnergy) +
    (internetRiskFactor * wInternet) +
    (climateRiskFactor * wClimate) +
    (regulatoryRiskFactor * wRegulatory) +
    (waterRiskFactor * wWater) +
    (supplyChainRiskFactor * wSupplyChain);

  // Apply a small floor for critical risks to ensure no zero risk if a major factor is very bad
  // For example, if energyReliability is 0 (energyRiskFactor is 10), it significantly impacts the score.
  // This ensures that an extremely high risk in one area can't be entirely offset.
  const criticalRiskFloor = 0;
  if (energyRiskFactor >= 9 || politicalRiskFactor >= 9 || internetRiskFactor >= 9) {
      criticalRiskFloor = 10;
  }
  weightedSum = Math.max(weightedSum, criticalRiskFloor); // Ensure minimum risk if any core factor is very poor

  // Step 4: Scale the overall risk score to 0-100
  // Max possible weighted sum is approximately 10 (if all risk factors are 10).
  // Min possible weighted sum is approximately 0 (if all risk factors are 0).
  let overallRiskScore = (weightedSum / 10) * 100;
  overallRiskScore = clamp(overallRiskScore, 0, 100); // Ensure score is between 0 and 100

  // Step 5: Calculate Operational Continuity Index
  let operationalContinuityIndex = 100 - overallRiskScore;
  operationalContinuityIndex = clamp(operationalContinuityIndex, 0, 100);

  // Step 6: Determine Long-Term Viability Rating
  let longTermViabilityRating;
  if (overallRiskScore <= 20) {
    longTermViabilityRating = "A (Excellent)";
  } else if (overallRiskScore <= 40) {
    longTermViabilityRating = "B (Good)";
  } else if (overallRiskScore <= 60) {
    longTermViabilityRating = "C (Moderate)";
  } else if (overallRiskScore <= 80) {
    longTermViabilityRating = "D (High Risk)";
  } else {
    longTermViabilityRating = "F (Extreme Risk)";
  }

  return {
    overallRiskScore: parseFloat(overallRiskScore.toFixed(1)),
    operationalContinuityIndex: parseFloat(operationalContinuityIndex.toFixed(1)),
    longTermViabilityRating: longTermViabilityRating
  };
}
