const { itLoad, coolingPower, otherFacilityPower, coolingSafetyMargin, operatingHours } = inputs;

const KW_TO_BTU_HR = 3412.14; // Conversion factor for 1 kW to BTU/hr

// Input validation and clamping
const safeITLoad = Math.max(0.1, itLoad); // Ensure IT load is at least 0.1 to prevent division by zero for PUE
const safeCoolingPower = Math.max(0, coolingPower);
const safeOtherFacilityPower = Math.max(0, otherFacilityPower);
const safeCoolingSafetyMargin = Math.max(0, coolingSafetyMargin);
const safeOperatingHours = Math.max(0, Math.min(24, operatingHours)); // Operating hours 0-24

// 1. Calculate Total Facility Power (TFP)
const totalFacilityPower = safeITLoad + safeCoolingPower + safeOtherFacilityPower;

// 2. Calculate PUE
const pue = totalFacilityPower / safeITLoad;

// 3. Calculate Required Cooling Capacity
// The heat generated that needs to be removed comes from IT equipment and other facility overhead.
// Cooling system power itself is used to *remove* this heat, not *add* to the heat inside the datacenter white space
// for the purpose of sizing cooling.
const totalHeatLoad_kW = safeITLoad + safeOtherFacilityPower;
const requiredCoolingCapacity = totalHeatLoad_kW * KW_TO_BTU_HR * (1 + safeCoolingSafetyMargin / 100);

// 4. Calculate Daily Energy Consumption
const dailyEnergyConsumption = totalFacilityPower * safeOperatingHours;

return {
    pue: pue,
    totalFacilityPower: totalFacilityPower,
    requiredCoolingCapacity: requiredCoolingCapacity,
    dailyEnergyConsumption: dailyEnergyConsumption
};
