if (itLoad <= 0) {
    return {
        currentPUE: NaN,
        annualEnergyConsumptionMWh: NaN,
        annualEnergyCost: NaN,
        optimizedAnnualEnergyConsumptionMWh: NaN,
        optimizedAnnualEnergyCost: NaN,
        annualSavingsPotential: NaN
    };
}

const totalPower = itLoad + nonItLoad;
const currentPUE = totalPower / itLoad;

const annualEnergyConsumptionKWH = totalPower * operatingHours;
const annualEnergyConsumptionMWh = annualEnergyConsumptionKWH / 1000;
const annualEnergyCost = annualEnergyConsumptionKWH * energyCostPerKWH;

let optimizedAnnualEnergyConsumptionMWh = annualEnergyConsumptionMWh;
let optimizedAnnualEnergyCost = annualEnergyCost;
let annualSavingsPotential = 0;

if (targetPUE && targetPUE >= 1) {
    const effectiveTargetPUE = Math.max(1, targetPUE); 
    const optimizedTotalPower = effectiveTargetPUE * itLoad;
    const optimizedAnnualEnergyConsumptionKWH = optimizedTotalPower * operatingHours;
    optimizedAnnualEnergyConsumptionMWh = optimizedAnnualEnergyConsumptionKWH / 1000;
    optimizedAnnualEnergyCost = optimizedAnnualEnergyConsumptionKWH * energyCostPerKWH;
    annualSavingsPotential = annualEnergyCost - optimizedAnnualEnergyCost;
} else if (pueImprovementPotential && pueImprovementPotential > 0) {
    const reductionFactor = Math.max(0, 1 - pueImprovementPotential / 100); 
    const reducedNonItLoad = nonItLoad * reductionFactor;
    const optimizedTotalPowerFromReduction = itLoad + reducedNonItLoad;
    const optimizedAnnualEnergyConsumptionKWH = optimizedTotalPowerFromReduction * operatingHours;
    optimizedAnnualEnergyConsumptionMWh = optimizedAnnualEnergyConsumptionKWH / 1000;
    optimizedAnnualEnergyCost = optimizedAnnualEnergyConsumptionKWH * energyCostPerKWH;
    annualSavingsPotential = annualEnergyCost - optimizedAnnualEnergyCost;
}

return {
    currentPUE: currentPUE,
    annualEnergyConsumptionMWh: annualEnergyConsumptionMWh,
    annualEnergyCost: annualEnergyCost,
    optimizedAnnualEnergyConsumptionMWh: optimizedAnnualEnergyConsumptionMWh,
    optimizedAnnualEnergyCost: optimizedAnnualEnergyCost,
    annualSavingsPotential: annualSavingsPotential
};
