const legalInitial = inputs.numJurisdictions * inputs.legalHoursPerJurisdiction * inputs.legalHourlyRate;
const infraInitial = (inputs.numDataSystems * 10000) + (inputs.numJurisdictions * inputs.avgMonthlyDataTB * inputs.infraCostPerTB * 6); // $10k per system for setup, plus 6 months of base infra per jurisdiction
const migrationInitial = inputs.numDataSystems * inputs.avgMonthlyDataTB * 250; // $250 per TB per system for migration effort
const initialSetupCost = legalInitial + infraInitial + migrationInitial;

const effectiveReplicationFactor = Math.max(1, inputs.replicationOverheadFactor); // Ensure factor is at least 1

const annualInfra = inputs.numJurisdictions * inputs.avgMonthlyDataTB * inputs.infraCostPerTB * 12 * effectiveReplicationFactor; // 12 months, factor for replication
const annualOps = inputs.numJurisdictions * inputs.numDataSystems * 2000; // $2k per year per jurisdiction per system for ops
const annualLegal = inputs.numJurisdictions * inputs.legalHourlyRate * 40; // 40 hours per year per jurisdiction for ongoing compliance
const annualRecurringCost = annualInfra + annualOps + annualLegal;

const total5YearCost = initialSetupCost + (annualRecurringCost * 5);

return {
  initialSetupCost: initialSetupCost,
  annualRecurringCost: annualRecurringCost,
  total5YearCost: total5YearCost
};
