if (dataVolume <= 0 || connectionSpeed <= 0) {
    return {
        timeInSeconds: 0,
        timeInMinutes: 0,
        timeInHours: 0,
        timeInDays: 0,
        error: "Data volume and connection speed must be positive numbers."
    };
}

let totalDataInBits;
const dataVolumeValue = parseFloat(dataVolume);
switch (dataVolumeUnit.toLowerCase()) {
    case 'kb': totalDataInBits = dataVolumeValue * 1024 * 8; break; // Kilobytes to bits (using 1024)
    case 'mb': totalDataInBits = dataVolumeValue * Math.pow(1024, 2) * 8; break; // Megabytes to bits
    case 'gb': totalDataInBits = dataVolumeValue * Math.pow(1024, 3) * 8; break; // Gigabytes to bits
    case 'tb': totalDataInBits = dataVolumeValue * Math.pow(1024, 4) * 8; break; // Terabytes to bits
    default: return {
        timeInSeconds: 0, timeInMinutes: 0, timeInHours: 0, timeInDays: 0,
        error: "Invalid data volume unit. Use KB, MB, GB, or TB."
    };
}

let transferSpeedInBitsPerSecond;
const connectionSpeedValue = parseFloat(connectionSpeed);
switch (connectionSpeedUnit.toLowerCase()) {
    case 'kbps': transferSpeedInBitsPerSecond = connectionSpeedValue * 1000; break; // Kilobits per second
    case 'mbps': transferSpeedInBitsPerSecond = connectionSpeedValue * Math.pow(1000, 2); break; // Megabits per second
    case 'gbps': transferSpeedInBitsPerSecond = connectionSpeedValue * Math.pow(1000, 3); break; // Gigabits per second
    case 'kb/s': transferSpeedInBitsPerSecond = connectionSpeedValue * 1024 * 8; break; // Kilobytes per second to bits/s
    case 'mb/s': transferSpeedInBitsPerSecond = connectionSpeedValue * Math.pow(1024, 2) * 8; break; // Megabytes per second to bits/s
    case 'gb/s': transferSpeedInBitsPerSecond = connectionSpeedValue * Math.pow(1024, 3) * 8; break; // Gigabytes per second to bits/s
    default: return {
        timeInSeconds: 0, timeInMinutes: 0, timeInHours: 0, timeInDays: 0,
        error: "Invalid connection speed unit. Use Kbps, Mbps, Gbps (for bits/s) or KB/s, MB/s, GB/s (for Bytes/s)."
    };
}

if (transferSpeedInBitsPerSecond <= 0) {
    return {
        timeInSeconds: 0, timeInMinutes: 0, timeInHours: 0, timeInDays: 0,
        error: "Connection speed must be greater than zero."
    };
}

let timeInSeconds = totalDataInBits / transferSpeedInBitsPerSecond;

const actualOverhead = Math.max(0, parseFloat(overheadPercentage || 0));
timeInSeconds = timeInSeconds * (1 + actualOverhead / 100);

const timeInMinutes = timeInSeconds / 60;
const timeInHours = timeInMinutes / 60;
const timeInDays = timeInHours / 24;

return {
    timeInSeconds: timeInSeconds,
    timeInMinutes: timeInMinutes,
    timeInHours: timeInHours,
    timeInDays: timeInDays,
};
