const size = parseFloat(inputs.fileSize) || 0;
const speed = parseFloat(inputs.speed) || 0;
const overhead = (parseFloat(inputs.overhead) || 0) / 100;

// Convert everything to Bits
let totalBits = 0;
if (inputs.fileUnit === 'MB') totalBits = size * 8 * 1000 * 1000;
if (inputs.fileUnit === 'GB') totalBits = size * 8 * 1000 * 1000 * 1000;
if (inputs.fileUnit === 'TB') totalBits = size * 8 * 1000 * 1000 * 1000 * 1000;
if (inputs.fileUnit === 'PB') totalBits = size * 8 * 1000 * 1000 * 1000 * 1000 * 1000;

// Convert speed to Bits/sec
let speedBps = 0;
if (inputs.speedUnit.includes('Mbps')) speedBps = speed * 1000 * 1000;
if (inputs.speedUnit.includes('Gbps')) speedBps = speed * 1000 * 1000 * 1000;
if (inputs.speedUnit.includes('MB/s')) speedBps = speed * 8 * 1000 * 1000;
if (inputs.speedUnit.includes('KB/s')) speedBps = speed * 8 * 1000;

if (speedBps === 0) return { transferTime: 0, seconds: 0, formattedTime: 'Infinity' };

// Add overhead (extra data transmitted)
const realBits = totalBits * (1 + overhead);
const seconds = realBits / speedBps;

// Format time string
const d = Math.floor(seconds / (3600*24));
const h = Math.floor(seconds % (3600*24) / 3600);
const m = Math.floor(seconds % 3600 / 60);
const s = Math.floor(seconds % 60);

let timeString = '';
if (d > 0) timeString += d + 'd ';
if (h > 0) timeString += h + 'h ';
if (m > 0) timeString += m + 'm ';
timeString += s + 's';

return {
    transferTime: seconds,
    seconds: seconds,
    formattedTime: timeString
};
