const principal = current_total_debt;
const annualRateCurrent = avg_current_interest_rate / 100;
const monthlyRateCurrent = annualRateCurrent / 12;
const currentMonthlyPayment = current_total_monthly_payment;

const annualRateNew = new_loan_interest_rate / 100;
const monthlyRateNew = annualRateNew / 12;
const newLoanTermMonths = new_loan_term_years * 12;
const fees = consolidation_fees || 0; 

let newMonthlyPayment = 0;
let totalNewLoanCost = 0;
let totalNewLoanInterest = 0;

// Calculate new monthly payment
if (monthlyRateNew === 0) {
    newMonthlyPayment = principal / newLoanTermMonths;
} else {
    newMonthlyPayment = (principal * monthlyRateNew) / (1 - Math.pow(1 + monthlyRateNew, -newLoanTermMonths));
}

totalNewLoanCost = newMonthlyPayment * newLoanTermMonths + fees;
totalNewLoanInterest = Math.max(0, totalNewLoanCost - principal); // Interest can't be negative

// Calculate current debt scenario (approximate effective term and total cost)
let effectiveCurrentTermMonths = 0;
let totalCurrentLoanCost = 0;
let totalCurrentLoanInterest = 0;

// Check if current payment even covers interest. Max term cap at 30 years (360 months).
const maxTermMonths = 360;

if (currentMonthlyPayment <= principal * monthlyRateCurrent && monthlyRateCurrent > 0) {
    // Payment doesn't cover interest, assume debt isn't being paid off or grows indefinitely.
    // For comparison, we'll use a very long term to represent the high cost.
    effectiveCurrentTermMonths = maxTermMonths; // Cap for calculation
    totalCurrentLoanCost = currentMonthlyPayment * effectiveCurrentTermMonths;
    totalCurrentLoanInterest = totalCurrentLoanCost - principal; 
} else if (monthlyRateCurrent === 0) {
    effectiveCurrentTermMonths = principal / currentMonthlyPayment;
    totalCurrentLoanCost = principal;
    totalCurrentLoanInterest = 0;
} else {
    // Calculate effective term for current debts based on current payment
    // n = -log(1 - (P*r)/PMT) / log(1+r)
    let calculatedTerm = -Math.log(1 - (principal * monthlyRateCurrent) / currentMonthlyPayment) / Math.log(1 + monthlyRateCurrent);
    effectiveCurrentTermMonths = Math.min(calculatedTerm, maxTermMonths); // Cap the term

    totalCurrentLoanCost = currentMonthlyPayment * effectiveCurrentTermMonths;
    totalCurrentLoanInterest = Math.max(0, totalCurrentLoanCost - principal); // Interest can't be negative
}

// Calculate savings
const monthlySavings = currentMonthlyPayment - newMonthlyPayment;
const totalCostSaved = totalCurrentLoanCost - totalNewLoanCost;
const totalInterestSaved = totalCurrentLoanInterest - totalNewLoanInterest;

return {
    new_monthly_payment: newMonthlyPayment,
    monthly_savings: monthlySavings,
    total_interest_saved: totalInterestSaved,
    total_cost_saved: totalCostSaved
};
