function calculatePayoff(principal, monthlyPayment, annualRate) {
    if (principal <= 0) return { months: 0, totalInterest: 0 };
    if (monthlyPayment <= 0) return { months: Infinity, totalInterest: Infinity };

    const monthlyRate = annualRate / 12 / 100;

    if (monthlyRate === 0) {
        // No interest scenario
        if (monthlyPayment >= principal) {
            return { months: principal / monthlyPayment, totalInterest: 0 };
        } else {
            return { months: Infinity, totalInterest: Infinity }; // Never paid off
        }
    }

    // Check if the payment covers at least the monthly interest
    if (monthlyPayment < principal * monthlyRate) {
        return { months: Infinity, totalInterest: Infinity };
    }

    // Amortization formula for number of payments (months)
    // n = -log(1 - (P * r) / M) / log(1 + r)
    const numMonths = -Math.log(1 - (principal * monthlyRate) / monthlyPayment) / Math.log(1 + monthlyRate);

    // Handle cases where numMonths might be NaN or not finite due to calculation limits
    if (isNaN(numMonths) || !isFinite(numMonths)) {
        return { months: Infinity, totalInterest: Infinity };
    }

    const totalPaid = numMonths * monthlyPayment;
    const totalInterest = totalPaid - principal;

    return { months: numMonths, totalInterest: Math.max(0, totalInterest) }; // Interest can't be negative
}

const totalDebtBalance = inputs.total_debt_balance;
const currentTotalMonthlyPayment = inputs.current_total_monthly_payment;
const averageInterestRate = inputs.average_interest_rate;
const additionalMonthlyPayment = inputs.additional_monthly_payment || 0;

// Calculate original scenario
const originalScenario = calculatePayoff(totalDebtBalance, currentTotalMonthlyPayment, averageInterestRate);
const originalPayoffMonths = originalScenario.months;
const originalTotalInterest = originalScenario.totalInterest;

// Calculate accelerated scenario
const newTotalMonthlyPayment = currentTotalMonthlyPayment + additionalMonthlyPayment;
const acceleratedScenario = calculatePayoff(totalDebtBalance, newTotalMonthlyPayment, averageInterestRate);
const acceleratedPayoffMonths = acceleratedScenario.months;
const acceleratedTotalInterest = acceleratedScenario.totalInterest;

const monthsSaved = originalPayoffMonths - acceleratedPayoffMonths;
const totalInterestSaved = originalTotalInterest - acceleratedTotalInterest;

return {
    original_payoff_months: Math.round(originalPayoffMonths),
    accelerated_payoff_months: Math.round(acceleratedPayoffMonths),
    months_saved: Math.round(monthsSaved),
    total_interest_saved: totalInterestSaved
};
