const createDebt = (balance, apr, minPayment) => {
    const b = Number(balance);
    const a = Number(apr);
    const m = Number(minPayment);
    // A debt is valid if balance is positive, APR is non-negative, and minPayment is positive
    if (isNaN(b) || isNaN(a) || isNaN(m) || b <= 0 || a < 0 || m <= 0) {
        return null; // Invalid debt
    }
    return { balance: b, apr: a, minPayment: m };
};

const calculatePayoff = (initialDebts, initialExtraPayment, sortFunction) => {
    let totalMonths = 0;
    let totalInterestPaid = 0;
    // currentExtraPaymentPool grows as minimum payments from paid-off debts are added
    let currentExtraPaymentPool = Number(initialExtraPayment) || 0;

    // Deep clone and filter out invalid debts
    let localDebts = initialDebts.map(d => ({ ...d }));
    localDebts = localDebts.filter(d => d.balance > 0 && d.apr >= 0 && d.minPayment > 0);

    if (localDebts.length === 0) {
        return { months: 0, interest: 0 };
    }

    // Cap the simulation to prevent infinite loops (e.g., if minPayment doesn't cover interest)
    while (localDebts.some(d => d.balance > 0) && totalMonths < 1200) { // Max 100 years
        totalMonths++;
        // Re-sort debts for each month based on the specific strategy
        localDebts.sort(sortFunction);

        // The target debt (first in the sorted list) receives its minimum payment PLUS the entire currentExtraPaymentPool.
        // All other active debts receive only their minimum payments.

        for (let i = 0; i < localDebts.length; i++) {
            let debt = localDebts[i];
            if (debt.balance <= 0) continue; // Skip already paid-off debts

            const monthlyInterestRate = debt.apr / 100 / 12;
            const interestThisMonth = debt.balance * monthlyInterestRate;
            totalInterestPaid += interestThisMonth;

            let paymentForThisDebt = debt.minPayment;

            // Apply extra payment only to the target debt
            if (i === 0) { 
                paymentForThisDebt += currentExtraPaymentPool;
            }

            // Ensure payment does not exceed the remaining balance + accrued interest for this debt
            paymentForThisDebt = Math.min(paymentForThisDebt, debt.balance + interestThisMonth);

            let principalPaid = paymentForThisDebt - interestThisMonth;
            debt.balance -= principalPaid; // Reduce balance by principal paid

            // If debt is paid off this month, add its minPayment to the extra payment pool
            if (debt.balance <= 0) {
                currentExtraPaymentPool += debt.minPayment; 
                debt.balance = 0; // Ensure balance is exactly zero, not negative
            }
        }
        // Filter out completely paid-off debts for the next iteration
        localDebts = localDebts.filter(d => d.balance > 0);

        if (localDebts.length === 0) break; // All debts have been paid off
    }

    return { months: totalMonths, interest: totalInterestPaid };
};

// Parse inputs, ensuring default values for optional fields
const inputExtraMonthlyPayment = Number(extraMonthlyPayment) || 0;

const debts = [
    createDebt(debt1Balance, debt1APR, debt1MinPayment),
    createDebt(debt2Balance, debt2APR, debt2MinPayment),
    createDebt(debt3Balance, debt3APR, debt3MinPayment)
].filter(d => d !== null); // Remove any null (invalid/unprovided) debt entries

// Sort function for Snowball: smallest balance first
const snowballSort = (a, b) => a.balance - b.balance;

// Sort function for Avalanche: highest APR first
const avalancheSort = (a, b) => b.apr - a.apr;

const snowballResult = calculatePayoff(debts, inputExtraMonthlyPayment, snowballSort);
const avalancheResult = calculatePayoff(debts, inputExtraMonthlyPayment, avalancheSort);

const snowballTimeYears = snowballResult.months / 12;
const avalancheTimeYears = avalancheResult.months / 12;

// Calculate interest and time saved, ensuring results are non-negative
const interestSaved = Math.max(0, snowballResult.interest - avalancheResult.interest);
const timeSavedMonths = Math.max(0, snowballResult.months - avalancheResult.months);

return {
    snowballTimeYears: snowballTimeYears,
    snowballTotalInterest: snowballResult.interest,
    avalancheTimeYears: avalancheTimeYears,
    avalancheTotalInterest: avalancheResult.interest,
    interestSaved: interestSaved,
    timeSavedMonths: timeSavedMonths
};
