function calculate() {
    const targetUserCount = parseFloat(inputs.targetUserCount);
    const requiredAvgBandwidthMbps = parseFloat(inputs.requiredAvgBandwidthMbps);
    const initialHardwareCostPerConnection = parseFloat(inputs.initialHardwareCostPerConnection);
    const monthlyServiceCostPerConnection = parseFloat(inputs.monthlyServiceCostPerConnection);
    const powerSolutionCostPerConnection = parseFloat(inputs.powerSolutionCostPerConnection);
    const deploymentComplexityFactor = parseFloat(inputs.deploymentComplexityFactor);
    const regulatoryCensorshipRisk = parseFloat(inputs.regulatoryCensorshipRisk);
    const projectLifespanYears = parseFloat(inputs.projectLifespanYears);
    const localMaintenanceAvailability = parseFloat(inputs.localMaintenanceAvailability);

    // Input validation and edge cases
    if (targetUserCount <= 0 || projectLifespanYears <= 0 || requiredAvgBandwidthMbps <= 0) {
        return {
            totalInitialDeploymentCost: 0,
            annualOperationalCost: 0,
            costPerUserPerMonth: 0,
            effectiveBandwidthPerUserMbps: 0,
            overallFeasibilityScore: 0
        };
    }

    // Step 1: Calculate Total Initial Deployment Cost
    const totalBaseHardwareCost = initialHardwareCostPerConnection * targetUserCount;
    const totalBasePowerCost = powerSolutionCostPerConnection * targetUserCount;

    // Deployment overhead: increases with complexity. Factor of 15% per complexity point above 1.
    const deploymentOverhead = (totalBaseHardwareCost + totalBasePowerCost) * Math.max(0, (deploymentComplexityFactor - 1)) * 0.15;

    // Regulatory risk buffer: increases with risk. Factor of 10% per risk point above 1.
    const regulatoryRiskBuffer = (totalBaseHardwareCost + totalBasePowerCost + deploymentOverhead) * Math.max(0, (regulatoryCensorshipRisk - 1)) * 0.1;

    const totalInitialDeploymentCost = totalBaseHardwareCost + totalBasePowerCost + deploymentOverhead + regulatoryRiskBuffer;

    // Step 2: Calculate Annual Operational Cost
    const totalMonthlyServiceCost = monthlyServiceCostPerConnection * targetUserCount;
    const baseAnnualMaintenance = (totalInitialDeploymentCost * 0.05); // 5% of initial cost as base annual maintenance

    // Maintenance complexity factor: low local availability increases cost.
    const maintenanceComplexityMultiplier = 1 + Math.max(0, (localMaintenanceAvailability - 1)) * 0.2; // 20% increase for Moderate, 40% for Low/None
    const annualMaintenanceCost = baseAnnualMaintenance * maintenanceComplexityMultiplier;

    const annualOperationalCost = (totalMonthlyServiceCost * 12) + annualMaintenanceCost;

    // Step 3: Calculate Cost Per User Per Month
    const totalProjectCost = totalInitialDeploymentCost + (annualOperationalCost * projectLifespanYears);
    const costPerUserPerMonth = totalProjectCost / (targetUserCount * projectLifespanYears * 12);

    // Step 4: Calculate Effective Bandwidth Per User
    const effectiveBandwidthPerUserMbps = Math.max(0.1, requiredAvgBandwidthMbps / targetUserCount); // Minimum of 0.1 Mbps per user

    // Step 5: Calculate Overall Feasibility Score (0-100)
    let rawScore = 0;

    // Cost Factor (costPerUserPerMonth)
    if (costPerUserPerMonth < 10) rawScore += 30; // Very affordable
    else if (costPerUserPerMonth < 30) rawScore += 15; // Affordable
    else if (costPerUserPerMonth < 50) rawScore += 5;  // Moderate
    else rawScore -= 20; // High cost, significant penalty

    // Bandwidth Factor (effectiveBandwidthPerUserMbps)
    if (effectiveBandwidthPerUserMbps > 5) rawScore += 25; // Excellent bandwidth
    else if (effectiveBandwidthPerUserMbps >= 2) rawScore += 15; // Good bandwidth
    else if (effectiveBandwidthPerUserMbps >= 0.5) rawScore += 5; // Basic bandwidth
    else rawScore -= 10; // Insufficient bandwidth, penalty

    // Deployment Complexity Factor
    if (deploymentComplexityFactor <= 2) rawScore += 15; // Easy deployment
    else if (deploymentComplexityFactor <= 3) rawScore += 5; // Moderate deployment
    else if (deploymentComplexityFactor <= 4) rawScore -= 10; // Challenging deployment
    else rawScore -= 20; // Extreme complexity, high penalty

    // Regulatory & Censorship Risk
    if (regulatoryCensorshipRisk <= 2) rawScore += 20; // Low risk
    else if (regulatoryCensorshipRisk <= 3) rawScore += 5; // Moderate risk
    else if (regulatoryCensorshipRisk <= 4) rawScore -= 15; // High risk
    else rawScore -= 30; // Extreme risk, very high penalty

    // Local Maintenance Availability
    if (localMaintenanceAvailability === 1) rawScore += 10; // High availability
    else if (localMaintenanceAvailability === 2) rawScore += 0; // Moderate availability (no bonus/penalty from base)
    else rawScore -= 15; // Low/None availability, penalty

    // Normalize score to 0-100
    const minRawScore = -95; // (worst case: -20 - 10 - 20 - 30 - 15)
    const maxRawScore = 100;  // (best case: 30 + 25 + 15 + 20 + 10)
    let overallFeasibilityScore = ((rawScore - minRawScore) / (maxRawScore - minRawScore)) * 100;
    overallFeasibilityScore = Math.round(Math.max(0, Math.min(100, overallFeasibilityScore)));

    return {
        totalInitialDeploymentCost: totalInitialDeploymentCost,
        annualOperationalCost: annualOperationalCost,
        costPerUserPerMonth: costPerUserPerMonth,
        effectiveBandwidthPerUserMbps: effectiveBandwidthPerUserMbps,
        overallFeasibilityScore: overallFeasibilityScore
    };
}
