const discountRate = 0.07; // Assumed annual discount rate (7%) for NPV calculation
const analysisPeriodYears = 10; // Standard analysis period for long-term investments

const initialInstallationCost = inputs.initialInstallationCost;
const totalIncentivesRebates = inputs.totalIncentivesRebates;
const annualElectricityConsumptionKWh = inputs.annualElectricityConsumptionKWh;
const currentElectricityRatePerKWh = inputs.currentElectricityRatePerKWh;
const annualOutageHours = inputs.annualOutageHours;
const costOfOutagePerHour = inputs.costOfOutagePerHour;
const annualMaintenanceCostDPC = inputs.annualMaintenanceCostDPC;
const annualInflationRate = inputs.annualInflationRate / 100; // Convert percentage to decimal

// 1. Calculate Effective Installation Cost (after incentives)
const effectiveInstallationCost = Math.max(0, initialInstallationCost - totalIncentivesRebates);

// 2. Calculate Annual Electricity Bill Savings (from self-generation)
const annualElectricityBillSavings = annualElectricityConsumptionKWh * currentElectricityRatePerKWh;

// 3. Calculate Annual Outage Cost Savings (monetized resilience benefit)
const annualOutageCostSavings = annualOutageHours * costOfOutagePerHour;

// 4. Calculate Total Annual Operational Savings (Year 1) - initial net savings
const totalAnnualOperationalSavings = annualElectricityBillSavings + annualOutageCostSavings - annualMaintenanceCostDPC;

// Handle cases where savings might be non-positive
if (totalAnnualOperationalSavings <= 0) {
    return {
        totalAnnualSavings: totalAnnualOperationalSavings,
        paybackPeriodYears: Infinity,
        netPresentValue: -effectiveInstallationCost,
        returnOnInvestment: -100 // Represents a full loss of initial investment
    };
}

// 5. Calculate Simple Payback Period
const paybackPeriodYears = effectiveInstallationCost / totalAnnualOperationalSavings;

// 6. Calculate Net Present Value (NPV) and Cumulative Cash Flows for ROI over the analysis period
let sumDiscountedCashFlows = 0;
let sumTotalCashFlows = 0;

for (let year = 1; year <= analysisPeriodYears; year++) {
    // Project savings for future years considering electricity price inflation
    // The `annualInflationRate` applies to the *monetary value* of the savings, not just electricity price.
    // This simplifies the model to show that the 'value' of savings increases with inflation.
    const projectedAnnualSavingsForYear = totalAnnualOperationalSavings * Math.pow(1 + annualInflationRate, year - 1);

    // Discount future cash flows to present value
    const discountedCashFlow = projectedAnnualSavingsForYear / Math.pow(1 + discountRate, year);
    sumDiscountedCashFlows += discountedCashFlow;
    sumTotalCashFlows += projectedAnnualSavingsForYear;
}

const netPresentValue = sumDiscountedCashFlows - effectiveInstallationCost;

// 7. Calculate Return on Investment (ROI)
// ROI = ((Total Cumulative Savings over Period - Effective Initial Investment) / Effective Initial Investment) * 100
const returnOnInvestment = ((sumTotalCashFlows - effectiveInstallationCost) / effectiveInstallationCost) * 100;

return {
    totalAnnualSavings: totalAnnualOperationalSavings,
    paybackPeriodYears: paybackPeriodYears,
    netPresentValue: netPresentValue,
    returnOnInvestment: returnOnInvestment
};
