function calculate({ proposedContractValue, historicalAverageCost, benchmarkCostPerUnit, numberOfUnits, strategicPriorityScore, riskAdjustmentFactor, performanceGainFactor, inflationRateAnnual }) {

  // Input validation and sanitization
  proposedContractValue = Math.max(0, proposedContractValue || 0);
  historicalAverageCost = Math.max(0, historicalAverageCost || 0);
  benchmarkCostPerUnit = Math.max(0, benchmarkCostPerUnit || 0);
  numberOfUnits = Math.max(0, numberOfUnits || 0);
  strategicPriorityScore = Math.max(0, Math.min(10, strategicPriorityScore || 0));
  riskAdjustmentFactor = Math.max(0, Math.min(100, riskAdjustmentFactor || 0));
  performanceGainFactor = Math.max(0, Math.min(100, performanceGainFactor || 0));
  inflationRateAnnual = Math.max(0, Math.min(20, inflationRateAnnual || 0));

  if (proposedContractValue === 0 && (historicalAverageCost === 0 || benchmarkCostPerUnit === 0 || numberOfUnits === 0)) {
    return {
      adjustedReferenceCost: 0,
      efficiencyRatio: 0,
      costSavingsPotential: 0,
      efficiencyRating: "Insufficient Data for Analysis"
    };
  }

  // Step 1: Adjust historical and benchmark costs for inflation
  const inflationFactor = 1 + (inflationRateAnnual / 100);
  const adjustedHistoricalTotalCost = historicalAverageCost * inflationFactor;
  let adjustedBenchmarkTotalCost = benchmarkCostPerUnit * numberOfUnits * inflationFactor;

  // Step 2: Apply risk and performance adjustments to the benchmark cost
  // Risk increases cost, performance gains decrease equivalent cost (better value for money)
  adjustedBenchmarkTotalCost *= (1 + (riskAdjustmentFactor / 100));
  adjustedBenchmarkTotalCost *= (1 - (performanceGainFactor / 100));

  // Ensure adjustedBenchmarkTotalCost does not become negative due to extreme performance gains
  adjustedBenchmarkTotalCost = Math.max(0, adjustedBenchmarkTotalCost);

  // Step 3: Determine the final Adjusted Reference Cost based on Strategic Priority Score
  // Higher strategic priority means greater emphasis on achieving benchmark efficiency.
  // strategicWeight ranges from 0 (purely historical) to 1 (purely benchmark).
  const strategicWeight = strategicPriorityScore / 10; 
  let finalAdjustedReferenceCost = 
    (adjustedHistoricalTotalCost * (1 - strategicWeight)) + 
    (adjustedBenchmarkTotalCost * strategicWeight);

  // Ensure reference cost is not zero or negative to prevent division errors and ensure meaningful comparison
  finalAdjustedReferenceCost = Math.max(1, finalAdjustedReferenceCost); // Cap at 1 to avoid division by zero if all inputs are tiny

  // Step 4: Calculate Efficiency Ratio and Potential Cost Savings
  const efficiencyRatio = proposedContractValue / finalAdjustedReferenceCost;
  const costSavingsPotential = finalAdjustedReferenceCost - proposedContractValue;

  // Step 5: Determine Efficiency Rating
  let efficiencyRating = "";
  if (efficiencyRatio <= 0.90) {
    efficiencyRating = "Highly Efficient (Significant Potential Savings)";
  } else if (efficiencyRatio > 0.90 && efficiencyRatio <= 1.05) {
    efficiencyRating = "Acceptably Efficient (Good Value for Money)";
  } else if (efficiencyRatio > 1.05 && efficiencyRatio <= 1.20) {
    efficiencyRating = "Suboptimal Efficiency (Review Needed)";
  } else {
    efficiencyRating = "Inefficient / High Risk (Significant Overspend Alert)";
  }

  if (proposedContractValue === 0 && finalAdjustedReferenceCost > 0) {
      efficiencyRating = "Exceptional Value (Potentially Free/Donated)";
  }

  return {
    adjustedReferenceCost: finalAdjustedReferenceCost,
    efficiencyRatio: efficiencyRatio,
    costSavingsPotential: costSavingsPotential,
    efficiencyRating: efficiencyRating
  };
}
