const { initialInvestment, rdDuration, innovationSuccessProbability, annualOperationalSavings, strategicCapabilityEnhancement, deploymentCostRatio, projectOperationalLifespan, discountRate } = inputs;

// Input validation and normalization
const inv = parseFloat(initialInvestment) || 0;
const duration = Math.max(0, parseInt(rdDuration) || 0);
const successProb = Math.max(0, Math.min(100, parseFloat(innovationSuccessProbability) || 0)) / 100; // Convert % to decimal, clamp 0-100
const opSavings = Math.max(0, parseFloat(annualOperationalSavings) || 0);
const stratEnhance = Math.max(1.0, parseFloat(strategicCapabilityEnhancement) || 1.0); // Minimum 1.0
const deployRatio = Math.max(0, parseFloat(deploymentCostRatio) || 0) / 100; // Convert % to decimal
const lifespan = Math.max(0, parseInt(projectOperationalLifespan) || 0);
const discRate = Math.max(0, parseFloat(discountRate) || 0) / 100; // Convert % to decimal

// Handle edge case where initial investment is zero, preventing division by zero later
if (inv === 0) {
    return {
        expectedROI: Infinity,
        netPresentValue: (opSavings * stratEnhance * successProb * lifespan),
        strategicValueScore: (successProb * 100 * stratEnhance) + (opSavings > 0 ? 100 : 0) // Boost score if benefits without cost
    };
}

// Step 1: Calculate Total Expected Costs
const totalRDCost = inv;
const totalDeploymentCost = inv * deployRatio;
const totalExpectedCosts = totalRDCost + totalDeploymentCost;

// Step 2: Calculate Expected Annual Benefits (adjusted by success probability and strategic factor)
// Annual raw benefits
const rawAnnualBenefits = opSavings * stratEnhance;
// Expected annual benefits if successful
const expectedAnnualBenefit = rawAnnualBenefits * successProb;

// Step 3: Calculate Total Expected Benefits over Project Lifespan
const totalExpectedBenefitsOverLifespan = expectedAnnualBenefit * lifespan;

// Step 4: Calculate Expected ROI
let expectedROI = 0;
if (totalExpectedCosts > 0) {
    expectedROI = ((totalExpectedBenefitsOverLifespan - totalExpectedCosts) / totalExpectedCosts) * 100;
} else if (totalExpectedBenefitsOverLifespan > 0) {
    expectedROI = Infinity; // Infinite ROI if costs are zero and benefits exist (already handled inv=0)
} else {
    expectedROI = 0; // No costs, no benefits (handled by inv=0 case if totalExpectedCosts is also 0)
}
// Cap ROI for extreme cases or set a floor
expectedROI = Math.min(expectedROI, 1000000); // Cap at 1,000,000% to prevent display issues
expectedROI = Math.max(expectedROI, -100); // Floor at -100% since loss cannot exceed initial investment (conceptually)

// Step 5: Calculate Net Present Value (NPV)
let npv = 0;
// Initial R&D investment (Year 0)
npv -= inv;

// Deployment costs (assumed to occur at the end of R&D duration)
const deploymentYear = duration;
if (deploymentYear >= 0) {
    npv -= totalDeploymentCost / Math.pow(1 + discRate, deploymentYear);
} else {
    // Should not happen with duration >= 0, but as fallback, assume immediate.
    npv -= totalDeploymentCost;
}

// Annual benefits discounted
if (discRate === 0) {
    // If discount rate is 0, simply add up the benefits after deploymentYear
    npv += expectedAnnualBenefit * lifespan;
} else {
    for (let i = 1; i <= lifespan; i++) {
        // Benefits start accruing *after* deployment, so year (deploymentYear + i)
        npv += expectedAnnualBenefit / Math.pow(1 + discRate, deploymentYear + i);
    }
}

// Strategic Value Score (a simple composite metric)
// Base score on success probability and strategic enhancement
let strategicValueScore = (successProb * 100 * stratEnhance);

// Add NPV contribution, scaled logarithmically for large values
if (npv > 0) {
    strategicValueScore += Math.log10(npv / 1000000 + 1) * 50; // Scale log contribution for meaningful impact
} else if (npv < 0) {
    strategicValueScore -= Math.log10(Math.abs(npv) / 1000000 + 1) * 25; // Penalize negative NPV
}

// Further adjust for very high or very low success probabilities
if (successProb < 0.1) strategicValueScore *= 0.5; // Halve score if very low success prob
if (successProb > 0.9) strategicValueScore *= 1.1; // Slight boost if very high success prob

// Cap the strategic value score to a reasonable range
strategicValueScore = Math.max(0, Math.min(strategicValueScore, 1000)); // Min 0, Max 1000

return {
    expectedROI: expectedROI,
    netPresentValue: npv,
    strategicValueScore: strategicValueScore
};
