const physicianScore = Math.min(10, inputs.physician_hours_per_week_per_100 / 2); 
const rnScore = Math.min(10, inputs.rn_hours_per_week_per_100 / 8); 
const specialistScore = Math.max(0, 10 - inputs.specialist_access_avg_wait_days / 5); 
const medicationScore = Math.min(10, inputs.medication_adherence_rate / 10); 
const incidentScore = Math.min(10, inputs.critical_incident_reporting_rate / 10); 
const mortalityScore = Math.max(0, 10 - inputs.excess_mortality_rate_deviation_pct / 2); 
const grievanceScore = Math.max(0, 10 - inputs.grievance_unresolved_rate); 
const telehealthScore = Math.min(10, inputs.telehealth_availability_score * 2); 

let rawQualityScore = (
    physicianScore * 0.15 + 
    rnScore * 0.15 + 
    specialistScore * 0.20 + 
    medicationScore * 0.10 + 
    incidentScore * 0.10 + 
    mortalityScore * 0.15 + 
    grievanceScore * 0.10 + 
    telehealthScore * 0.05
);

const qualityScore = Math.round(rawQualityScore * 10); 

let riskLevel = 4; // Critical
if (qualityScore >= 80) {
    riskLevel = 1; // Low
} else if (qualityScore >= 60) {
    riskLevel = 2; // Moderate
} else if (qualityScore >= 40) {
    riskLevel = 3; // High
}

return {
    quality_score: qualityScore,
    risk_level: riskLevel
};
