function calculateRisk(inputs) {
    const age = inputs.age;
    const biologicalSex = inputs.biologicalSex;
    const weightKg = inputs.weightKg;
    const heightCm = inputs.heightCm;
    const dailyAddedSugarGrams = inputs.dailyAddedSugarGrams;
    const sugaryDrinksPerWeek = inputs.sugaryDrinksPerWeek;
    const processedSnacksPerWeek = inputs.processedSnacksPerWeek;
    const physicalActivityLevel = inputs.physicalActivityLevel;
    const familyHistoryDiabetesCVD = inputs.familyHistoryDiabetesCVD;
    const existingMetabolicConditions = inputs.existingMetabolicConditions;

    // --- Input Validation and Defaulting --- 
    // (Frontend validation should handle most, but robust backend check)
    if (age < 18 || age > 100) throw new Error("Age must be between 18 and 100.");
    if (weightKg <= 0 || weightKg > 250) throw new Error("Weight must be realistic.");
    if (heightCm <= 0 || heightCm > 220) throw new Error("Height must be realistic.");
    if (dailyAddedSugarGrams < 0 || dailyAddedSugarGrams > 200) throw new Error("Daily added sugar intake must be between 0 and 200 grams.");

    // --- Step 1: Calculate BMI --- 
    const heightM = heightCm / 100;
    const bmi = weightKg / (heightM * heightM);

    // --- Step 2: Initialize Risk Score based on baseline factors --- 
    let metabolicImpactScore = 0;

    // Age contribution
    if (age >= 60) metabolicImpactScore += 10;
    else if (age >= 40) metabolicImpactScore += 5;

    // Biological Sex (general metabolic differences, subtle effect)
    if (biologicalSex === "Female") metabolicImpactScore += 2;

    // BMI contribution
    if (bmi >= 30) metabolicImpactScore += 20; // Obese
    else if (bmi >= 25) metabolicImpactScore += 10; // Overweight
    else if (bmi < 18.5) metabolicImpactScore += 5; // Underweight can also indicate metabolic stress

    // Existing Metabolic Conditions
    if (existingMetabolicConditions === "Yes") metabolicImpactScore += 25; // Significant factor for current risk

    // Family History
    if (familyHistoryDiabetesCVD === "Yes") metabolicImpactScore += 15;
    else if (familyHistoryDiabetesCVD === "Unsure") metabolicImpactScore += 5; // Slight increase for unknown risk

    // --- Step 3: Quantify Direct Sugar Impact from daily intake and consumption frequency --- 
    // Daily Added Sugar Grams (WHO recommends <25g/day for optimal health. AHA recommends <36g for men, <25g for women. Use 25g as general threshold)
    if (dailyAddedSugarGrams > 25) {
        // Each gram above 25g adds points, capped to prevent extreme scores from very high intake
        metabolicImpactScore += Math.min((dailyAddedSugarGrams - 25) * 0.8, 40); 
    }

    // Sugary Drinks per Week (each drink contributes significantly)
    if (sugaryDrinksPerWeek >= 5) metabolicImpactScore += 15;
    else if (sugaryDrinksPerWeek >= 2) metabolicImpactScore += 8;
    else if (sugaryDrinksPerWeek >= 1) metabolicImpactScore += 3;

    // Processed Snacks/Desserts per Week
    if (processedSnacksPerWeek >= 7) metabolicImpactScore += 12; // Daily consumption
    else if (processedSnacksPerWeek >= 3) metabolicImpactScore += 7;
    else if (processedSnacksPerWeek >= 1) metabolicImpactScore += 2;

    // --- Step 4: Adjust risk based on physical activity level --- 
    // Physical activity level: 1=sedentary, 2=light, 3=moderate, 4=active, 5=very active
    if (physicalActivityLevel === 5) metabolicImpactScore -= 15;
    else if (physicalActivityLevel === 4) metabolicImpactScore -= 10;
    else if (physicalActivityLevel === 3) metabolicImpactScore -= 5;
    else if (physicalActivityLevel === 1) metabolicImpactScore += 5; // Sedentary lifestyle increases risk

    // --- Step 5: Consolidate all factors into a final metabolic impact score and categorize risk --- 
    metabolicImpactScore = Math.max(0, Math.min(100, Math.round(metabolicImpactScore))); // Cap score between 0 and 100

    let longTermHealthRiskCategory;
    if (metabolicImpactScore >= 75) {
        longTermHealthRiskCategory = "Very High";
    } else if (metabolicImpactScore >= 50) {
        longTermHealthRiskCategory = "High";
    } else if (metabolicImpactScore >= 25) {
        longTermHealthRiskCategory = "Moderate";
    } else {
        longTermHealthRiskCategory = "Low";
    }

    // --- Step 6: Convert total sugar grams to equivalent teaspoons for easier understanding --- 
    const equivalentTeaspoonsSugarPerDay = dailyAddedSugarGrams / 4; // 1 teaspoon = approx 4 grams of sugar

    return {
        metabolicImpactScore: metabolicImpactScore,
        longTermHealthRiskCategory: longTermHealthRiskCategory,
        equivalentTeaspoonsSugarPerDay: parseFloat(equivalentTeaspoonsSugarPerDay.toFixed(1))
    };
}
