function calculateRisk(inputs) {
  // Validate inputs and set defaults if necessary (though 'required' should handle basic presence)
  const govTransparency = Math.min(100, Math.max(0, inputs.govSurveillanceTransparencyIndex || 50));
  const dataRetention = Math.min(60, Math.max(0, inputs.dataRetentionPeriodMonths || 12)); // Cap retention at 60 months for calculation impact
  const corporateSharing = Math.min(100, Math.max(0, inputs.corporateDataSharingScore || 50));
  const judicialOversight = Math.min(100, Math.max(0, inputs.judicialOversightEffectiveness || 50));
  const encryptionStandard = Math.min(100, Math.max(0, inputs.platformEncryptionStandard || 50));
  const whistleblowerProtection = Math.min(100, Math.max(0, inputs.whistleblowerProtectionScore || 50));
  const moderationImpact = Math.min(100, Math.max(0, inputs.userContentModerationImpact || 50));

  // Normalize inputs to a common scale where higher values generally indicate 'better' for civil liberties
  // and then invert for risk calculation where higher means more risk.

  // 1. Government Surveillance Transparency (Higher transparency = Lower risk)
  // Risk contribution: (100 - transparency) * weight
  const transparencyRisk = (100 - govTransparency) * 0.25; 

  // 2. Data Retention Period (Longer retention = Higher risk)
  // Map 0-60 months to a 0-100 risk scale. 0 months = 0 risk, 60+ months = 100 risk contribution
  const retentionRisk = (dataRetention / 60) * 100 * 0.20; // 60 months max considered high risk

  // 3. Corporate Platform Data Sharing (Higher score = Lower sharing = Lower risk)
  // Risk contribution: (100 - sharing_score) * weight
  const corporateSharingRisk = (100 - corporateSharing) * 0.15;

  // 4. Judicial Oversight Effectiveness (Higher effectiveness = Lower risk)
  // Risk contribution: (100 - oversight) * weight
  const judicialOversightRisk = (100 - judicialOversight) * 0.15;

  // 5. Platform Encryption Standard (Higher standard = Lower risk)
  // Risk contribution: (100 - encryption_standard) * weight
  const encryptionRisk = (100 - encryptionStandard) * 0.10; 

  // 6. Whistleblower Protection Index (Higher protection = Lower risk of unreported abuses)
  // Risk contribution: (100 - protection) * weight
  const whistleblowerRisk = (100 - whistleblowerProtection) * 0.05;

  // 7. Impact of Content Moderation on User Speech (Higher score = Less arbitrary moderation = Lower risk)
  // Risk contribution: (100 - moderation_impact) * weight
  const moderationRisk = (100 - moderationImpact) * 0.10;

  // Aggregate total risk score
  let totalRisk = transparencyRisk + retentionRisk + corporateSharingRisk + 
                  judicialOversightRisk + encryptionRisk + whistleblowerRisk + moderationRisk;

  // Cap risk score between 0 and 100
  totalRisk = Math.min(100, Math.max(0, totalRisk));

  // Determine risk level
  let riskLevel;
  if (totalRisk >= 80) {
    riskLevel = "Critical";
  } else if (totalRisk >= 60) {
    riskLevel = "High";
  } else if (totalRisk >= 40) {
    riskLevel = "Moderate";
  } else if (totalRisk >= 20) {
    riskLevel = "Low";
  } else {
    riskLevel = "Very Low";
  }

  return {
    digitalCivilLibertiesRiskScore: Math.round(totalRisk),
    riskLevel: riskLevel
  };
}
