function calculateFraudRisk({ audienceSize, avgEngagementRate, revenueDiversityScore, followerGrowthAnomaly, reportedScams, platformSecurityRating, contentHighRiskCategory, sponsoredContentRatio }) {
    let riskScore = 0; // Initialize risk score

    // --- 1. Audience Size Factor (Influence on overall impact, not direct fraud risk itself) ---
    // Larger audiences attract more scrutiny but also offer larger targets. Logarithmic scale.
    // Max 5 points. Minimum audience size for calculation is 1000 to avoid negative log values or small number bias.
    let audienceRisk = Math.min(Math.log10(Math.max(1000, audienceSize)) - 3, 5) * 1; // e.g., 1000 -> 0; 100k -> 2; 10M -> 4
    riskScore += Math.max(0, audienceRisk); // Ensure non-negative

    // --- 2. Engagement Rate Factor (Lower engagement suggests bots/fake audience) ---
    // Ideal engagement rate is often 1-5%. Below 1% is high risk, above 10% can be suspicious too.
    // Penalize low engagement more heavily. Max 25 points.
    let engagementRisk = 0;
    if (avgEngagementRate < 0.5) { // Very low engagement, almost certainly bots
        engagementRisk = (0.5 - avgEngagementRate) * 25; 
    } else if (avgEngagementRate < 2) { // Low engagement
        engagementRisk = (2 - avgEngagementRate) * 10;
    } else if (avgEngagementRate > 15) { // Unnaturally high engagement (e.g., comment pods, small niche, or manipulation)
        engagementRisk = (avgEngagementRate - 15) * 0.5; // Slight penalty
    }
    riskScore += Math.min(engagementRisk, 25);

    // --- 3. Revenue Stream Diversity Factor (Lack of diversity increases vulnerability) ---
    // 1 (very low) to 5 (very high). Lower score = higher risk. Max 15 points.
    let diversityRisk = (5 - revenueDiversityScore) * 3;
    riskScore += Math.min(diversityRisk, 15);

    // --- 4. Follower Growth Anomaly Factor (Sudden spikes suggest botting or inorganic growth) ---
    // Percentage increase above average. Max 20 points.
    let growthAnomalyRisk = Math.min(followerGrowthAnomaly / 2.5, 20); // 50% spike = 20 points
    riskScore += Math.max(0, growthAnomalyRisk); // Ensure non-negative

    // --- 5. Reported Scam/Fraud Incidents Factor (Direct evidence of past issues) ---
    // Each incident adds significant risk. Max 30 points.
    let reportedScamRisk = reportedScams * 10;
    riskScore += Math.min(reportedScamRisk, 30);

    // --- 6. Platform Security & Moderation Rating Factor (Lower rating means easier for fraud) ---
    // 1 (poor) to 5 (excellent). Lower score = higher risk. Max 10 points.
    let platformSecurityRisk = (5 - platformSecurityRating) * 2;
    riskScore += Math.min(platformSecurityRisk, 10);

    // --- 7. Content in High-Risk Category Factor (Certain niches attract more fraud) ---
    // Boolean. If true, add fixed risk. Max 15 points.
    if (contentHighRiskCategory) {
        riskScore += 15;
    }

    // --- 8. Sponsored Content Ratio Factor (Extremes can indicate fraud or misrepresentation) ---
    // Too low might mean difficulty attracting sponsors or hiding sponsored content.
    // Too high might indicate a 'sell-out' mentality or lack of authentic content.
    // Max 10 points.
    let sponsoredRisk = 0;
    if (sponsoredContentRatio < 5) { // Extremely low sponsored content
        sponsoredRisk = (5 - sponsoredContentRatio) * 1;
    } else if (sponsoredContentRatio > 75) { // Very high sponsored content, potential over-commercialization
        sponsoredRisk = (sponsoredContentRatio - 75) * 0.4; 
    }
    riskScore += Math.min(sponsoredRisk, 10);

    // --- Total Score Normalization and Categorization ---
    // Maximum theoretical raw score with these caps: 
    // Audience: 5
    // Engagement: 25
    // Diversity: 15
    // Anomaly: 20
    // Scams: 30
    // Platform Security: 10
    // High Risk Category: 15
    // Sponsored: 10
    // Max theoretical raw score = 5 + 25 + 15 + 20 + 30 + 10 + 15 + 10 = 130
    // Normalize to 0-100.
    let fraudRiskScore = Math.min(Math.max(0, riskScore), 130); // Cap raw score to 0-130
    fraudRiskScore = (fraudRiskScore / 130) * 100; // Scale to 0-100

    let riskCategory;
    if (fraudRiskScore >= 75) {
        riskCategory = "Critical";
    } else if (fraudRiskScore >= 50) {
        riskCategory = "High";
    } else if (fraudRiskScore >= 25) {
        riskCategory = "Moderate";
    } else {
        riskCategory = "Low";
    }

    return { fraudRiskScore: parseFloat(fraudRiskScore.toFixed(2)), riskCategory };
}
