function calculate() {
  let rawScore = 0;

  // Points for Data Collection Scope (higher scope = higher risk)
  rawScore += (inputs.dataCollectionScope - 1) * 3;

  // Points for Data Sharing Extent (more sharing = higher risk)
  rawScore += (inputs.dataSharingExtent - 1) * 4;

  // Points for Encryption In Transit (no encryption = higher risk)
  rawScore += (inputs.encryptionInTransit === 0 ? 10 : 0);

  // Points for Encryption At Rest (no encryption = higher risk)
  rawScore += (inputs.encryptionAtRest === 0 ? 10 : 0);

  // Points for User Consent Mechanism (weaker mechanism = higher risk)
  rawScore += (5 - inputs.userConsentMechanism) * 3;

  // Points for HIPAA Compliance (non-compliant = higher risk)
  rawScore += (inputs.hipaaCompliance === 0 ? 15 : 0);

  // Points for GDPR Compliance (non-compliant = higher risk)
  rawScore += (inputs.gdprCompliance === 0 ? 15 : 0);

  // Points for Data Retention Policy (unclear/indefinite = higher risk)
  rawScore += (5 - inputs.dataRetentionPolicy) * 2;

  // Points for Third-Party Audits (no audits = higher risk)
  rawScore += (inputs.thirdPartyAudits === 0 ? 8 : 0);

  // Points for Anonymization Practices (weak/none = higher risk)
  rawScore += (5 - inputs.anonymizationPractices) * 2;

  // Maximum possible raw score based on the scoring system:
  // (5-1)*3=12 (Data Collection)
  // (5-1)*4=16 (Data Sharing)
  // 10 (No Enc. In Transit)
  // 10 (No Enc. At Rest)
  // (5-1)*3=12 (Weak Consent)
  // 15 (No HIPAA)
  // 15 (No GDPR)
  // (5-1)*2=8 (Bad Retention)
  // 8 (No Audits)
  // (5-1)*2=8 (No Anonymization)
  // Total Max Raw Score = 12 + 16 + 10 + 10 + 12 + 15 + 15 + 8 + 8 + 8 = 114
  const maxPossibleRawScore = 114;

  // Normalize the score to a 0-100 scale
  let privacyRiskScore = (rawScore / maxPossibleRawScore) * 100;

  // Ensure score is within 0-100 bounds
  privacyRiskScore = Math.max(0, Math.min(100, privacyRiskScore));

  let riskLevel;
  if (privacyRiskScore <= 20) {
    riskLevel = "Low";
  } else if (privacyRiskScore <= 40) {
    riskLevel = "Moderate";
  } else if (privacyRiskScore <= 70) {
    riskLevel = "High";
  } else {
    riskLevel = "Critical";
  }

  return {
    privacyRiskScore: Math.round(privacyRiskScore),
    riskLevel: riskLevel
  };
}
