function calculate({ wcag_compliance_level, screen_reader_compatibility_score, keyboard_navigation_score, caption_transcript_availability_score, content_readability_score, design_consistency_score, mobile_responsiveness_score, bandwidth_flexibility_score, technical_support_accessibility_score, user_feedback_integration_score }) {

    // Input validation and clamping (edge cases)
    const clamp = (val, min, max) => Math.min(Math.max(val, min), max);

    const wcag = clamp(wcag_compliance_level, 0, 10);
    const sr = clamp(screen_reader_compatibility_score, 0, 10);
    const kn = clamp(keyboard_navigation_score, 0, 10);
    const ct = clamp(caption_transcript_availability_score, 0, 10);
    const cr = clamp(content_readability_score, 0, 10);
    const dc = clamp(design_consistency_score, 0, 10);
    const mr = clamp(mobile_responsiveness_score, 0, 10);
    const bf = clamp(bandwidth_flexibility_score, 0, 10);
    const ts = clamp(technical_support_accessibility_score, 0, 10);
    const uf = clamp(user_feedback_integration_score, 0, 10);

    // Normalize scores to a 0-1 scale for calculation
    const wcag_norm = wcag / 10;
    const sr_norm = sr / 10;
    const kn_norm = kn / 10;
    const ct_norm = ct / 10;
    const cr_norm = cr / 10;
    const dc_norm = dc / 10;
    const mr_norm = mr / 10;
    const bf_norm = bf / 10;
    const ts_norm = ts / 10;
    const uf_norm = uf / 10;

    // Calculate contributions based on weighted percentages (summing to 100)
    // Weights: WCAG=25%, SR=15%, KN=15%, CT=10%, CR=10%, DC=5%, MR=5%, BF=5%, TS=5%, UF=5%
    const wcag_contrib = wcag_norm * 25;
    const sr_contrib = sr_norm * 15;
    const kn_contrib = kn_norm * 15;
    const ct_contrib = ct_norm * 10;
    const cr_contrib = cr_norm * 10;
    const dc_contrib = dc_norm * 5;
    const mr_contrib = mr_norm * 5;
    const bf_contrib = bf_norm * 5;
    const ts_contrib = ts_norm * 5;
    const uf_contrib = uf_norm * 5;

    const total_accessibility_score = 
        wcag_contrib + sr_contrib + kn_contrib + ct_contrib +
        cr_contrib + dc_contrib + mr_contrib + bf_contrib +
        ts_contrib + uf_contrib;

    // Calculate category contributions for detailed insights
    const wcag_contribution = wcag_contrib;
    const core_functional_usability_contribution = sr_contrib + kn_contrib + ct_contrib;
    const content_design_usability_contribution = cr_contrib + dc_contrib;
    const technical_support_equity_contribution = mr_contrib + bf_contrib + ts_contrib + uf_contrib;

    let equity_readiness_level = "Critical Gaps - Urgent Action Required";
    if (total_accessibility_score >= 85) {
        equity_readiness_level = "Excellent - Highly Equitable & Accessible";
    } else if (total_accessibility_score >= 70) {
        equity_readiness_level = "Good - Generally Accessible";
    } else if (total_accessibility_score >= 50) {
        equity_readiness_level = "Developing - Moderate Gaps Identified";
    } else if (total_accessibility_score >= 30) {
        equity_readiness_level = "Significant Gaps - Major Improvements Needed";
    }

    return {
        total_accessibility_score: parseFloat(total_accessibility_score.toFixed(2)),
        wcag_contribution: parseFloat(wcag_contribution.toFixed(2)),
        core_functional_usability_contribution: parseFloat(core_functional_usability_contribution.toFixed(2)),
        content_design_usability_contribution: parseFloat(content_design_usability_contribution.toFixed(2)),
        technical_support_equity_contribution: parseFloat(technical_support_equity_contribution.toFixed(2)),
        equity_readiness_level: equity_readiness_level
    };
}
