function calculate({ sourceCredibility, biasRecognition, factChecking, dataInterpretation, hoaxIdentification, emotionalResponse, sharingResponsibility }) {

  // Input validation and clamping to ensure scores are within 1-5
  const clamp = (num, min, max) => Math.min(Math.max(num, min), max);

  const sC = clamp(sourceCredibility, 1, 5);
  const bR = clamp(biasRecognition, 1, 5);
  const fC = clamp(factChecking, 1, 5);
  const dI = clamp(dataInterpretation, 1, 5);
  const hI = clamp(hoaxIdentification, 1, 5);
  const eR = clamp(emotionalResponse, 1, 5);
  const sR = clamp(sharingResponsibility, 1, 5);

  // Max possible score for 7 inputs, each 5 points
  const MAX_TOTAL_SCORE = 35;

  // Calculate raw scores
  const totalRawScore = sC + bR + fC + dI + hI + eR + sR;

  // Calculate overall score (scaled to 100)
  const overallScore = Math.round((totalRawScore / MAX_TOTAL_SCORE) * 100);

  // Critical Thinking components: sourceCredibility, biasRecognition, dataInterpretation, emotionalResponse
  const criticalThinkingRaw = sC + bR + dI + eR;
  const MAX_CRITICAL_THINKING_SCORE = 4 * 5; // 4 inputs, max 5 points each
  const criticalThinkingScore = Math.round((criticalThinkingRaw / MAX_CRITICAL_THINKING_SCORE) * 100);

  // Fact-Checking & Verification components: factChecking, hoaxIdentification, sharingResponsibility
  const factCheckingRaw = fC + hI + sR;
  const MAX_FACT_CHECKING_SCORE = 3 * 5; // 3 inputs, max 5 points each
  const factCheckingScore = Math.round((factCheckingRaw / MAX_FACT_CHECKING_SCORE) * 100);

  // Determine recommendation level based on overall score
  let recommendationLevel;
  if (overallScore <= 40) {
    recommendationLevel = "Novice Navigator: Significant improvement needed in evaluating digital content.";
  } else if (overallScore <= 60) {
    recommendationLevel = "Emerging Evaluator: Developing skills, but still susceptible to misinformation. Focus on active practice.";
  } else if (overallScore <= 80) {
    recommendationLevel = "Proficient Ponderer: Generally skilled in media literacy, capable of independent evaluation. Refine specific areas.";
  } else {
    recommendationLevel = "Master Media Analyst: Highly adept at critical evaluation and fact-checking. A valuable asset in the information ecosystem.";
  }

  return {
    overallScore,
    criticalThinkingScore,
    factCheckingScore,
    recommendationLevel
  };
}
