function calculate({
    desiredOutputPower,
    dewEfficiency,
    engagementDuration,
    engagementsPerHour,
    auxiliaryPower,
    storageEfficiency,
    generationReliabilityFactor,
    peakPowerBuffer
}) {
    // Input validation and conversion
    desiredOutputPower = parseFloat(desiredOutputPower);
    dewEfficiency = parseFloat(dewEfficiency);
    engagementDuration = parseFloat(engagementDuration);
    engagementsPerHour = parseInt(engagementsPerHour);
    auxiliaryPower = parseFloat(auxiliaryPower);
    storageEfficiency = parseFloat(storageEfficiency);
    generationReliabilityFactor = parseFloat(generationReliabilityFactor);
    peakPowerBuffer = parseFloat(peakPowerBuffer);

    // Edge case handling for efficiencies and durations
    if (desiredOutputPower <= 0) {
        throw new Error("Desired DEW Output Power must be greater than 0.");
    }
    if (dewEfficiency <= 0 || dewEfficiency >= 1) {
        throw new Error("DEW System Overall Efficiency must be between 0 and 1 (exclusive).");
    }
    if (engagementDuration <= 0) {
        throw new Error("Operational Duration per Engagement must be greater than 0.");
    }
    if (engagementsPerHour <= 0) {
        throw new Error("Maximum Engagements per Hour must be greater than 0.");
    }
    if (storageEfficiency <= 0 || storageEfficiency >= 1) {
        throw new Error("Energy Storage System Round-Trip Efficiency must be between 0 and 1 (exclusive).");
    }
    if (generationReliabilityFactor < 1) {
        throw new Error("Power Generation System Reliability Factor must be 1 or greater.");
    }
    if (peakPowerBuffer < 0 || peakPowerBuffer >= 1) {
        throw new Error("Peak Power Buffer must be between 0 and 1 (exclusive).");
    }


    // Step 1: Calculate the instantaneous electrical input power required for the DEW itself
    // Input Power (kW) = Output Power (kW) / Efficiency
    const dewInputPower = desiredOutputPower / dewEfficiency;

    // Step 2: Calculate the total peak electrical power requirement
    // This includes the DEW input power, auxiliary systems, and applies a peak power buffer and reliability factor.
    const basePeakPower = dewInputPower + auxiliaryPower;
    const totalPeakPower = basePeakPower * (1 + peakPowerBuffer) * generationReliabilityFactor;

    // Step 3: Calculate the energy required per engagement from the energy storage system
    // Energy (kJ) = Power (kW) * Duration (s)
    // Convert to MJ and account for storage discharge losses: MJ = (kJ / 1000) / storageEfficiency_discharge
    // Assuming storageEfficiency is round-trip, we apply it fully for simplicity here for discharge
    // For strict interpretation: energy required *from* storage: (dewInputPower * engagementDuration / 1000) MJ
    // If storageEfficiency is round-trip, then the energy *stored* would be higher than energy_drawn/efficiency_discharge.
    // Let's assume the input `storageEfficiency` refers to the overall efficiency of storing AND retrieving energy, 
    // so the energy *to be stored* for a given *output* energy is energy_output / efficiency.
    const energyConsumedByDEW_kJ = dewInputPower * engagementDuration;
    const energyToStoreForEngagement_MJ = (energyConsumedByDEW_kJ / 1000) / storageEfficiency; 

    // Step 4: Calculate the total hourly energy throughput required from the power generator
    // This includes energy for all engagements within an hour and continuous auxiliary power,
    // accounting for storage round-trip efficiency for charging and a reliability factor.
    const energyToChargeStorageHourly_MJ = (energyConsumedByDEW_kJ / 1000) * engagementsPerHour / storageEfficiency;
    const auxiliaryEnergyHourly_MJ = auxiliaryPower * 3600 / 1000; // kW * s/hr / 1000 = MJ/hr

    const totalHourlyEnergyThroughput_MJ = (energyToChargeStorageHourly_MJ + auxiliaryEnergyHourly_MJ) * generationReliabilityFactor;

    // Step 5: Calculate the sustained power generation capacity needed
    // This is the average power required to meet the total hourly energy throughput.
    // Sustained Power (kW) = (Total Hourly Energy Throughput (MJ/hr) * 1000 kJ/MJ) / 3600 s/hr
    const sustainedGenerationCapacity = (totalHourlyEnergyThroughput_MJ * 1000) / 3600;

    return {
        totalPeakPower: totalPeakPower,
        energyPerEngagement: energyToStoreForEngagement_MJ,
        totalHourlyEnergyThroughput: totalHourlyEnergyThroughput_MJ,
        sustainedGenerationCapacity: sustainedGenerationCapacity
    };
}
