function calculate({ householdSize, homeValue, wildfireRiskLevel, earthquakeRiskLevel, floodRiskLevel, desiredSupplyDays, homeAgeYears, propertyAcreage, currentPreparednessSpend }) {
    // Constants for calculation logic
    const SUPPLY_COST_PER_PERSON_PER_DAY = 25; // Estimate for food, water, basic first aid
    const STRUCTURAL_BASE_COST_FACTOR_HOME_VALUE = 0.005; // 0.5% of home value as base for structural upgrades
    const STRUCTURAL_RISK_MULTIPLIER_EQ = [0, 1, 1.5, 2.5, 4, 6]; // Multipliers for earthquake risk levels 1-5
    const STRUCTURAL_AGE_ADD_PER_YEAR = 0.02; // Additional 2% for every year a home is old, on a base factor
    const STRUCTURAL_AGE_MULTIPLIER_CAP = 3; // Maximum age multiplier for structural costs
    const WILDFIRE_MITIGATION_COST_PER_ACRE_BASE = 1500; // Base cost for defensible space per acre
    const WILDFIRE_RISK_MULTIPLIER = [0, 1, 1.5, 2.5, 4, 6]; // Multipliers for wildfire risk levels 1-5
    const WILDFIRE_MITIGATION_CAP = 25000; // Cap for wildfire mitigation costs
    const FLOOD_MITIGATION_BASE_COST = 2000; // Base cost for initial flood barriers/sealing
    const FLOOD_RISK_MULTIPLIER = [0, 1, 2, 3.5, 5, 7]; // Multipliers for flood risk levels 1-5
    const FLOOD_MITIGATION_CAP = 15000; // Cap for flood mitigation costs
    const INSURANCE_BASE_PREMIUM_INCREASE_FACTOR = 0.0005; // 0.05% of home value annually as a base insurance increase
    const INSURANCE_RISK_ADJUSTMENT_PER_LEVEL_FACTOR = 0.0001; // 0.01% of home value per risk level increase per disaster type
    const INSURANCE_PREMIUM_INCREASE_CAP_FACTOR = 0.015; // Cap insurance increase at 1.5% of home value
    const ANNUAL_MITIGATION_MAINTENANCE_FACTOR = 0.05; // 5% of initial property mitigation for annual upkeep

    // Input validation and normalization to prevent unexpected values and ensure calculations are robust
    householdSize = Math.max(1, householdSize || 1);
    homeValue = Math.max(50000, homeValue || 50000); // Minimum home value set
    wildfireRiskLevel = Math.min(5, Math.max(1, wildfireRiskLevel || 1));
    earthquakeRiskLevel = Math.min(5, Math.max(1, earthquakeRiskLevel || 1));
    floodRiskLevel = Math.min(5, Math.max(1, floodRiskLevel || 1));
    desiredSupplyDays = Math.min(30, Math.max(3, desiredSupplyDays || 7)); // Cap days at 30, min at 3
    homeAgeYears = Math.max(0, homeAgeYears || 0);
    propertyAcreage = Math.max(0, propertyAcreage || 0);
    currentPreparednessSpend = Math.max(0, currentPreparednessSpend || 0);

    // Step 1: Calculate Emergency Supplies Budget
    let emergencySuppliesBudget = householdSize * desiredSupplyDays * SUPPLY_COST_PER_PERSON_PER_DAY;
    emergencySuppliesBudget = Math.round(emergencySuppliesBudget / 100) * 100; // Round to nearest $100

    // Step 2: Calculate Structural Reinforcements Budget (primarily earthquake, adjusted by age)
    let structuralUpgradesBudget = homeValue * STRUCTURAL_BASE_COST_FACTOR_HOME_VALUE; // Base cost tied to home value
    structuralUpgradesBudget *= STRUCTURAL_RISK_MULTIPLIER_EQ[earthquakeRiskLevel]; // Adjust by earthquake risk level
    let ageMultiplier = 1 + (homeAgeYears * STRUCTURAL_AGE_ADD_PER_YEAR);
    ageMultiplier = Math.min(ageMultiplier, STRUCTURAL_AGE_MULTIPLIER_CAP); // Cap the age multiplier
    structuralUpgradesBudget *= ageMultiplier;
    structuralUpgradesBudget = Math.round(structuralUpgradesBudget / 1000) * 1000; // Round to nearest $1000
    structuralUpgradesBudget = Math.min(structuralUpgradesBudget, homeValue * 0.15); // Cap structural upgrades at 15% of home value

    // Step 3: Calculate Property Mitigation Budget (Wildfire and Flood)
    let propertyMitigationWildfire = propertyAcreage * WILDFIRE_MITIGATION_COST_PER_ACRE_BASE * WILDFIRE_RISK_MULTIPLIER[wildfireRiskLevel];
    propertyMitigationWildfire = Math.min(propertyMitigationWildfire, WILDFIRE_MITIGATION_CAP); // Apply wildfire mitigation cap

    let propertyMitigationFlood = 0;
    if (floodRiskLevel > 1) { // Only incur flood costs if risk is not minimal (level 1 implies very low risk, minimal action needed)
        propertyMitigationFlood = FLOOD_MITIGATION_BASE_COST * FLOOD_RISK_MULTIPLIER[floodRiskLevel];
    }
    propertyMitigationFlood = Math.min(propertyMitigationFlood, FLOOD_MITIGATION_CAP); // Apply flood mitigation cap

    let propertyMitigationBudget = propertyMitigationWildfire + propertyMitigationFlood;
    propertyMitigationBudget = Math.round(propertyMitigationBudget / 500) * 500; // Round to nearest $500

    // Step 4: Calculate Estimated Annual Insurance Premium Increase
    let insurancePremiumIncrease = homeValue * INSURANCE_BASE_PREMIUM_INCREASE_FACTOR; // Base increase
    // Add additional increase based on combined risk levels
    insurancePremiumIncrease += (wildfireRiskLevel + earthquakeRiskLevel + floodRiskLevel) * homeValue * INSURANCE_RISK_ADJUSTMENT_PER_LEVEL_FACTOR;
    insurancePremiumIncrease = Math.round(insurancePremiumIncrease / 10) * 10; // Round to nearest $10
    insurancePremiumIncrease = Math.min(insurancePremiumIncrease, homeValue * INSURANCE_PREMIUM_INCREASE_CAP_FACTOR); // Cap insurance increase

    // Step 5: Calculate Total Estimated Initial Investment
    let totalEstimatedInitialCost = emergencySuppliesBudget + structuralUpgradesBudget + propertyMitigationBudget;
    // Subtract any current year's spend, ensuring the result is not negative
    totalEstimatedInitialCost = Math.max(0, totalEstimatedInitialCost - currentPreparednessSpend);

    // Step 6: Calculate Annual Recurring Cost (Insurance + Property Maintenance)
    let annualRecurringCost = insurancePremiumIncrease + (propertyMitigationBudget * ANNUAL_MITIGATION_MAINTENANCE_FACTOR);
    annualRecurringCost = Math.round(annualRecurringCost / 50) * 50; // Round to nearest $50

    // Return all calculated outputs
    return {
        totalEstimatedInitialCost: totalEstimatedInitialCost,
        annualRecurringCost: annualRecurringCost,
        emergencySuppliesBudget: emergencySuppliesBudget,
        structuralUpgradesBudget: structuralUpgradesBudget,
        propertyMitigationBudget: propertyMitigationBudget,
        insurancePremiumIncrease: insurancePremiumIncrease
    };
}
