const SRP = data.standardRepairCostPercentage / 100;
const RRP = data.resilientRepairCostPercentage / 100;
const SIP = data.standardInsurancePremiumPercentage / 100;
const RIP = data.resilientInsurancePremiumPercentage / 100;
const AIR = data.annualInflationRate / 100;

const buildingAreaSqM = Math.max(0, data.buildingAreaSqM);
const projectedLifespanYears = Math.max(1, data.projectedLifespanYears);
const standardMaterialCostPerSqM = Math.max(0, data.standardMaterialCostPerSqM);
const resilientMaterialCostPerSqM = Math.max(0, data.resilientMaterialCostPerSqM);

const avgDisasterFrequencyYears = data.avgDisasterFrequencyYears > 0 ? data.avgDisasterFrequencyYears : 1; // Cap at 1 year minimum frequency if 0 or negative input

const totalUpfrontCostStandard = standardMaterialCostPerSqM * buildingAreaSqM;
const totalUpfrontCostResilient = resilientMaterialCostPerSqM * buildingAreaSqM;

// Base building value for percentage calculations (use resilient if higher, represents asset value for damage assessment)
const baseBuildingValue = Math.max(totalUpfrontCostStandard, totalUpfrontCostResilient);

let totalRepairCostStandardLifetime = 0;
let totalRepairCostResilientLifetime = 0;
let totalInsuranceCostStandardLifetime = 0;
let totalInsuranceCostResilientLifetime = 0;

let currentStandardCostAccumulator = totalUpfrontCostStandard;
let currentResilientCostAccumulator = totalUpfrontCostResilient;
let paybackPeriodYears = Infinity;

if (buildingAreaSqM <= 0 || projectedLifespanYears <= 0) {
    return {
        totalUpfrontCostStandard: 0,
        totalUpfrontCostResilient: 0,
        totalRepairCostStandardLifetime: 0,
        totalRepairCostResilientLifetime: 0,
        totalInsuranceCostStandardLifetime: 0,
        totalInsuranceCostResilientLifetime: 0,
        totalLifetimeCostStandard: 0,
        totalLifetimeCostResilient: 0,
        netLifetimeSavingsResilient: 0,
        paybackPeriodYears: 0
    };
}

for (let year = 1; year <= projectedLifespanYears; year++) {
    // Inflation factor applies to costs incurred in a given year. For year 1, inflationFactor = (1+AIR)^0 = 1.
    // For year 'n', inflationFactor = (1+AIR)^(n-1) if applied at beginning of year, or (1+AIR)^n if at end.
    // We'll use (year - 1) for simplicity to represent inflation up to the beginning of the year's expense.
    const inflationFactor = Math.pow(1 + AIR, year - 1); 

    // Annual Insurance Premiums (inflated)
    const annualInsurancePremiumStandard = baseBuildingValue * SIP * inflationFactor;
    const annualInsurancePremiumResilient = baseBuildingValue * RIP * inflationFactor;
    totalInsuranceCostStandardLifetime += annualInsurancePremiumStandard;
    totalInsuranceCostResilientLifetime += annualInsurancePremiumResilient;

    currentStandardCostAccumulator += annualInsurancePremiumStandard;
    currentResilientCostAccumulator += annualInsurancePremiumResilient;

    // Repair Costs (if a disaster occurs in this year based on frequency)
    // The check `Math.abs(eventCount - Math.round(eventCount)) < 0.0001` determines if 'year' is a multiple of 'avgDisasterFrequencyYears',
    // thus indicating a disaster event according to the average frequency.
    const eventCount = year / avgDisasterFrequencyYears;
    if (Math.abs(eventCount - Math.round(eventCount)) < 0.0001) {
        const repairCostStandard = baseBuildingValue * SRP * inflationFactor;
        const repairCostResilient = baseBuildingValue * RRP * inflationFactor;
        totalRepairCostStandardLifetime += repairCostStandard;
        totalRepairCostResilientLifetime += repairCostResilient;

        currentStandardCostAccumulator += repairCostStandard;
        currentResilientCostAccumulator += repairCostResilient;
    }

    // Check for payback period: when the cumulative cost of resilient becomes less than or equal to standard.
    if (paybackPeriodYears === Infinity && currentResilientCostAccumulator <= currentStandardCostAccumulator) {
        paybackPeriodYears = year;
    }
}

// 2. Total Lifetime Costs
const totalLifetimeCostStandard = totalUpfrontCostStandard + totalRepairCostStandardLifetime + totalInsuranceCostStandardLifetime;
const totalLifetimeCostResilient = totalUpfrontCostResilient + totalRepairCostResilientLifetime + totalInsuranceCostResilientLifetime;

// 3. Net Lifetime Savings
const netLifetimeSavingsResilient = totalLifetimeCostStandard - totalLifetimeCostResilient;

// Adjust payback period for initial upfront cost difference and overall savings
if (totalUpfrontCostResilient <= totalUpfrontCostStandard) {
    paybackPeriodYears = 0; // If resilient is cheaper or equal upfront, payback is immediate.
} else if (paybackPeriodYears === Infinity && netLifetimeSavingsResilient <= 0) {
    paybackPeriodYears = Infinity; // If no payback within lifespan and no overall savings, payback is 'Never'.
} else if (paybackPeriodYears === Infinity && netLifetimeSavingsResilient > 0) {
    // This scenario implies that even though there are overall lifetime savings, the crossover point
    // was not met within the 'projectedLifespanYears' iteratively. It would take longer.
    paybackPeriodYears = Infinity; 
}

return {
    totalUpfrontCostStandard: totalUpfrontCostStandard,
    totalUpfrontCostResilient: totalUpfrontCostResilient,
    totalRepairCostStandardLifetime: totalRepairCostStandardLifetime,
    totalRepairCostResilientLifetime: totalRepairCostResilientLifetime,
    totalInsuranceCostStandardLifetime: totalInsuranceCostStandardLifetime,
    totalInsuranceCostResilientLifetime: totalInsuranceCostResilientLifetime,
    totalLifetimeCostStandard: totalLifetimeCostStandard,
    totalLifetimeCostResilient: totalLifetimeCostResilient,
    netLifetimeSavingsResilient: netLifetimeSavingsResilient,
    paybackPeriodYears: paybackPeriodYears === Infinity ? 'Never' : parseFloat(paybackPeriodYears.toFixed(1))
};
