const { shelter_length, shelter_width, shelter_height, wall_r_value, roof_r_value, floor_r_value, internal_temp, external_temp } = inputs;

// Calculate areas
const wallArea = 2 * (shelter_length + shelter_width) * shelter_height;
const roofArea = shelter_length * shelter_width;
const floorArea = shelter_length * shelter_width; // Assuming floor area equals roof area

// Calculate U-values from R-values (U = 1/R)
// Ensure R-values are not zero or negative to prevent division by zero or negative U-values.
// A very small R-value (e.g., 0.01) represents very poor insulation.
const minRValue = 0.01; // Cap R-value to prevent U from becoming infinite or excessively large
const uWall = 1 / Math.max(wall_r_value, minRValue);
const uRoof = 1 / Math.max(roof_r_value, minRValue);
const uFloor = 1 / Math.max(floor_r_value, minRValue);

// Calculate temperature difference. Use absolute value as heat can flow both ways.
const tempDifference = Math.abs(internal_temp - external_temp);

// Calculate heat transfer for each surface (Q = U * A * dT)
const qWall = uWall * wallArea * tempDifference;
const qRoof = uRoof * roofArea * tempDifference;
const qFloor = uFloor * floorArea * tempDifference;

// Total heat transfer rate (Watts)
const totalHeatTransferWatts = qWall + qRoof + qFloor;

// Estimated daily energy demand (kWh/day)
// 1 Watt = 1 Joule/second
// Watts to kWh/day: Watts * 24 hours / 1000 (for kW)
const dailyEnergyDemandKwh = (totalHeatTransferWatts * 24) / 1000; // kWh per day

return {
  total_heat_transfer: totalHeatTransferWatts,
  daily_energy_demand: dailyEnergyDemandKwh,
};
