function calculate({
  communityAnnualDemand_kWh,
  peakDemand_kW,
  solarCapacity_kW,
  windCapacity_kW,
  batteryStorage_kWh,
  gridElectricityCost_per_kWh,
  demandCharge_per_kW_month,
  initialInvestmentCost_MUSD,
  opexAnnualRate_percent,
  projectLifespan_years
}) {
  // Constants and Assumptions
  const hoursPerYear = 8760;
  const solarCapacityFactor = 0.18; // Average annual capacity factor for solar PV
  const windCapacityFactor = 0.35; // Average annual capacity factor for onshore wind
  const batteryDischargeDuration_hours = 4; // Assumed battery discharge duration for peak shaving
  const gridCarbonIntensity_kgCO2_per_kWh = 0.4; // Average US grid carbon intensity

  // Input validation and defaults
  communityAnnualDemand_kWh = Math.max(0, communityAnnualDemand_kWh || 0);
  peakDemand_kW = Math.max(0, peakDemand_kW || 0);
  solarCapacity_kW = Math.max(0, solarCapacity_kW || 0);
  windCapacity_kW = Math.max(0, windCapacity_kW || 0);
  batteryStorage_kWh = Math.max(0, batteryStorage_kWh || 0);
  gridElectricityCost_per_kWh = Math.max(0, gridElectricityCost_per_kWh || 0);
  demandCharge_per_kW_month = Math.max(0, demandCharge_per_kW_month || 0);
  initialInvestmentCost_MUSD = Math.max(0, initialInvestmentCost_MUSD || 0);
  opexAnnualRate_percent = Math.max(0, opexAnnualRate_percent || 0);
  projectLifespan_years = Math.max(1, projectLifespan_years || 1); // Minimum 1 year lifespan

  // 1. Calculate Local Energy Generation
  const annualSolarGeneration_kWh = solarCapacity_kW * hoursPerYear * solarCapacityFactor;
  const annualWindGeneration_kWh = windCapacity_kW * hoursPerYear * windCapacityFactor;
  const totalLocalGeneration_kWh = annualSolarGeneration_kWh + annualWindGeneration_kWh;

  // 2. Self-Sufficiency Ratio
  const selfSufficiencyRatio = communityAnnualDemand_kWh > 0 ? 
    Math.min(1, totalLocalGeneration_kWh / communityAnnualDemand_kWh) * 100 : 0;

  // 3. Annual Energy Savings
  const potentialAvoidedGridEnergy_kWh = Math.min(communityAnnualDemand_kWh, totalLocalGeneration_kWh);
  const energySavings_USD = potentialAvoidedGridEnergy_kWh * gridElectricityCost_per_kWh;

  // 4. Demand Charge Reduction Savings
  // Assume battery can discharge at its effective power rating for peak shaving
  const effectiveBatteryDischargePower_kW = batteryStorage_kWh / batteryDischargeDuration_hours;
  const peakDemandReduction_kW = Math.min(peakDemand_kW, effectiveBatteryDischargePower_kW);
  const demandChargeSavings_USD = peakDemandReduction_kW * demandCharge_per_kW_month * 12; // 12 months in a year

  // 5. Total Annual Savings (Gross)
  const totalAnnualSavings_USD = energySavings_USD + demandChargeSavings_USD;

  // 6. Annual O&M Costs
  const initialInvestment_USD = initialInvestmentCost_MUSD * 1_000_000;
  const annualOpex_USD = initialInvestment_USD * (opexAnnualRate_percent / 100);

  // 7. Net Annual Savings
  const netAnnualSavings_USD = totalAnnualSavings_USD - annualOpex_USD;

  // 8. Simple Payback Period
  let paybackPeriod_years = Infinity;
  if (netAnnualSavings_USD > 0) {
    paybackPeriod_years = initialInvestment_USD / netAnnualSavings_USD;
  } else if (netAnnualSavings_USD <= 0 && initialInvestment_USD > 0) {
    paybackPeriod_years = -1; // Indicate no positive payback
  } else {
    paybackPeriod_years = 0; // No investment, immediate payback or no savings/investment case
  }

  // 9. Simplified Levelized Cost of Energy (LCOE)
  // This is a highly simplified LCOE, neglecting time value of money and complex financial modeling.
  let LCOE_per_kWh = Infinity;
  const totalEnergyOverLifespan_kWh = totalLocalGeneration_kWh * projectLifespan_years;
  const totalCostOverLifespan_USD = initialInvestment_USD + (annualOpex_USD * projectLifespan_years);

  if (totalEnergyOverLifespan_kWh > 0) {
    LCOE_per_kWh = totalCostOverLifespan_USD / totalEnergyOverLifespan_kWh;
  } else if (totalCostOverLifespan_USD > 0) {
    LCOE_per_kWh = Infinity; // Costs, but no generation
  } else {
    LCOE_per_kWh = 0; // No cost, no generation (or both zero)
  }

  // 10. Estimated Annual CO2 Reduction
  const carbonReduction_tons_CO2 = potentialAvoidedGridEnergy_kWh * gridCarbonIntensity_kgCO2_per_kWh / 1000;

  return {
    selfSufficiencyRatio: selfSufficiencyRatio,
    annualEnergySavings_USD: annualEnergySavings_USD,
    annualOpex_USD: annualOpex_USD,
    netAnnualSavings_USD: netAnnualSavings_USD,
    paybackPeriod_years: paybackPeriod_years,
    LCOE_per_kWh: LCOE_per_kWh,
    carbonReduction_tons_CO2: carbonReduction_tons_CO2
  };
}
