function calculateDDM(inputs) {
    const D0 = inputs.currentDividend;
    const g1 = inputs.highGrowthRate; // high growth rate (e.g., 0.10 for 10%)
    const N = inputs.highGrowthYears; // years of high growth
    const g2 = inputs.terminalGrowthRate; // perpetual growth rate (e.g., 0.03 for 3%)
    const r = inputs.requiredRateOfReturn; // required rate of return (e.g., 0.12 for 12%)

    if (r <= g2) {
        throw new Error("The Required Rate of Return must be greater than the Terminal Growth Rate for the model to be valid.");
    }
    if (g1 < -1 || g2 < -1 || r < 0) {
        throw new Error("Growth rates cannot be less than -100% and required rate of return cannot be negative.");
    }
    if (N < 0) {
        throw new Error("Years of high growth cannot be negative.");
    }

    let pvHighGrowthDividends = 0;
    let lastDividendInHighGrowth = D0;

    // Calculate present value of dividends during high growth phase
    for (let t = 1; t <= N; t++) {
        const dividend_t = D0 * Math.pow(1 + g1, t);
        pvHighGrowthDividends += dividend_t / Math.pow(1 + r, t);
        if (t === N) {
            lastDividendInHighGrowth = dividend_t;
        }
    }

    // Calculate Terminal Value (TV) at the end of the high growth phase (Year N)
    // TV_N = D_(N+1) / (r - g2) = (D_N * (1 + g2)) / (r - g2)
    const dividend_Nplus1 = lastDividendInHighGrowth * (1 + g2);
    const terminalValue_N = dividend_Nplus1 / (r - g2);

    // Discount Terminal Value back to present
    const pvTerminalValue = terminalValue_N / Math.pow(1 + r, N);

    const intrinsicValue = pvHighGrowthDividends + pvTerminalValue;

    return {
        intrinsicValue: intrinsicValue,
        pvHighGrowthDividends: pvHighGrowthDividends,
        pvTerminalValue: pvTerminalValue
    };
}
