const { initial_investment, avg_annual_dividend_yield, annual_dividend_growth_rate, annual_contribution, reinvestment_rate, projection_period, tax_rate_on_dividends } = inputs;

let currentPortfolioValue = initial_investment;
let currentEffectiveYield = avg_annual_dividend_yield / 100;
const annualContributionAmount = annual_contribution;
const dividendGrowthFactor = 1 + (annual_dividend_growth_rate / 100);
const reinvestmentFactor = reinvestment_rate / 100;
const taxFactor = 1 - (tax_rate_on_dividends / 100);

let totalDividendsReceivedBeforeTax = 0;
let totalDividendsReceivedAfterTax = 0;

for (let year = 1; year <= projection_period; year++) {
    // Calculate dividends generated this year based on current portfolio value and effective yield
    const dividendsGeneratedThisYear = currentPortfolioValue * currentEffectiveYield;

    // Calculate amounts reinvested and received
    const dividendsReinvestedThisYear = dividendsGeneratedThisYear * reinvestmentFactor;
    const dividendsReceivedBeforeTaxThisYear = dividendsGeneratedThisYear * (1 - reinvestmentFactor);
    const dividendsReceivedAfterTaxThisYear = dividendsReceivedBeforeTaxThisYear * taxFactor;

    // Accumulate totals
    totalDividendsReceivedBeforeTax += dividendsReceivedBeforeTaxThisYear;
    totalDividendsReceivedAfterTax += dividendsReceivedAfterTaxThisYear;

    // Update portfolio value: add reinvested dividends and new contributions
    currentPortfolioValue += dividendsReinvestedThisYear;
    currentPortfolioValue += annualContributionAmount;

    // Update effective yield for the next year (due to dividend growth)
    currentEffectiveYield *= dividendGrowthFactor;
}

// Final calculations after the loop
const finalPortfolioValue = currentPortfolioValue;
// The annual dividend income generated by the portfolio at the end of the projection period
// based on the final portfolio value and the final effective yield.
const projectedAnnualDividendIncomeAtEnd = finalPortfolioValue * currentEffectiveYield;

return {
    total_portfolio_value_end: finalPortfolioValue,
    annual_dividend_income_end: projectedAnnualDividendIncomeAtEnd,
    total_dividends_received: totalDividendsReceivedBeforeTax,
    total_dividends_after_tax: totalDividendsReceivedAfterTax
};
