const initial = parseFloat(inputs.initialInvestment) || 0;
const contrib = parseFloat(inputs.annualContribution) || 0;
const yieldPct = (parseFloat(inputs.dividendYield) || 0) / 100;
const divGrowth = (parseFloat(inputs.dividendGrowth) || 0) / 100;
const capApp = (parseFloat(inputs.capitalAppreciation) || 0) / 100;
const years = parseFloat(inputs.years) || 10;
const tax = (parseFloat(inputs.taxRate) || 0) / 100;
const reinvest = inputs.drip === 'Yes';

let shares = initial; // Tracking value effectively
let currentYield = yieldPct;
let totalCostBasis = initial;

// Simplification: We track portfolio value and 'effective yield' separately
// But accurate tracking requires separating Price Growth vs Dividend Growth
// Let's model it year by year

let portfolioValue = initial;
let currentAnnualDividend = portfolioValue * yieldPct;

for (let i = 1; i <= years; i++) {
    // 1. Calculate Dividends for the year
    // Base dividends on start-of-year value * current yield
    let grossDivs = portfolioValue * yieldPct;

    // 2. Tax
    let netDivs = grossDivs * (1 - tax);

    // 3. Reinvest or Withdraw
    if (reinvest) {
        portfolioValue += netDivs;
    }

    // 4. Add Contribution
    portfolioValue += contrib;
    totalCostBasis += contrib;
    if (reinvest) totalCostBasis += netDivs; // If drip, cost basis increases by reinvested amount

    // 5. Capital Appreciation (Stock Price Growth)
    portfolioValue *= (1 + capApp);

    // 6. Dividend Growth
    // If the stock price grows by 5% and dividend grows by 6%, yield actually changes slightly
    // But standard model: Yield = Div / Price.
    // We model Dividend $ growing by 'divGrowth'.
    // Wait, simpler approach for this calculator type:
    // We track 'Income Potential' separately if we want precision on 'Yield on Cost'

    // Let's refine: The dividend PAYOUT per share grows by divGrowth.
    // The SHARE PRICE grows by capApp.
    // Yield at year N = (InitialYield * (1+divGrowth)^N) / ((1+capApp)^N)
    // This gets complex. Let's stick to the standard 'Total Return' method but track Income.

    // Recalculate Yield based on growth spread
    // New Yield = Old Yield * (1+DivGrowth) / (1+PriceGrowth)
    yieldPct = yieldPct * (1 + divGrowth) / (1 + capApp);
}

const finalAnnualIncome = portfolioValue * yieldPct;
const yieldOnCost = (finalAnnualIncome / totalCostBasis) * 100;

return {
    finalAnnualIncome: finalAnnualIncome,
    totalPortfolioValue: portfolioValue,
    yieldOnCost: yieldOnCost
};
