const weightLbs = parseFloat(inputs.dogWeight) || 0;
const amountOz = parseFloat(inputs.chocolateAmount) || 0;
const type = inputs.chocolateType;

// Theobromine content (mg per oz)
// Source: Merck Vet Manual
let mgPerOz = 0;
if (type.includes('White')) mgPerOz = 0.25;
else if (type.includes('Milk')) mgPerOz = 44;
else if (type.includes('Dark')) mgPerOz = 150;
else if (type.includes('Baking')) mgPerOz = 390;
else if (type.includes('Cocoa')) mgPerOz = 700;

const totalTheobromine = amountOz * mgPerOz;
const weightKg = weightLbs / 2.20462;
const dose = totalTheobromine / weightKg; // mg/kg

let risk = '';
let symp = '';
let rec = '';

if (dose < 20) {
    risk = 'Low Risk';
    symp = 'Mild stomach upset possible.';
    rec = 'Monitor at home. If vomiting persists, call vet.';
} else if (dose < 40) {
    risk = 'Moderate Risk';
    symp = 'Vomiting, diarrhea, hyperactivity, high heart rate.';
    rec = 'Contact veterinarian immediately. Treatment may be needed.';
} else if (dose < 60) {
    risk = 'High Risk';
    symp = 'Tremors, seizures, racing heart.';
    rec = 'EMERGENCY. Go to vet immediately.';
} else {
    risk = 'Severe / Critical';
    symp = 'Seizures, heart failure, coma.';
    rec = 'LIFE THREATENING. RUSH TO EMERGENCY VET.';
}

return {
    toxicityLevel: risk,
    symptoms: symp,
    recommendation: rec
};
