const loopLength_ft = inputs.loopLength;
const pipeDiameter_in = inputs.pipeDiameter;
const pipeRoughness_ft = inputs.pipeRoughness;
const tempDrop_F = inputs.tempDrop;
const avgWaterTemp_F = inputs.avgWaterTemp;
const equivalentLengthFittings_ft = inputs.equivalentLengthFittings;
const insulationRValue_h_ft2_F_Btu_in = inputs.insulationRValue;
const ambientTemp_F = inputs.ambientTemp;

// --- Constants (US customary units) ---
const g = 32.174; // ft/s^2 (gravitational acceleration)
const cp_water = 1.0; // Btu/(lb*°F) (specific heat of water)
const gallons_per_ft3 = 7.48052; // gallons per cubic foot

// --- Water properties at avgWaterTemp_F (approximations) ---
let rho_water_lb_ft3; // density
let mu_water_lb_ft_sec; // dynamic viscosity

if (avgWaterTemp_F < 100) {
    rho_water_lb_ft3 = 62.2; // approx at 80F
    mu_water_lb_ft_sec = 1.8e-5; // approx at 80F
} else if (avgWaterTemp_F < 140) {
    rho_water_lb_ft3 = 62.1; // approx at 120F
    mu_water_lb_ft_sec = 1.45e-5; // approx at 120F
} else {
    rho_water_lb_ft3 = 61.8; // approx at 160F
    mu_water_lb_ft_sec = 1.1e-5; // approx at 160F
}

// Ensure positive temperature difference for heat loss
const tempDifference_water_ambient = Math.max(0.1, avgWaterTemp_F - ambientTemp_F); // Avoid division by zero or negative heat loss

// --- Derived properties and unit conversions ---
const pipeDiameter_ft = pipeDiameter_in / 12; // Convert inches to feet

// --- 1. Heat Loss Calculation (Q_loss) ---
// Simplified U-value calculation (Btu/(hr*ft^2*°F))
// R_surface_air is the thermal resistance of the air film on the outside of the insulation.
// Assumed insulation thickness is 1 inch for the provided R-value.
const R_surface_air = 0.6; // h*ft^2*°F/Btu (approx for natural convection to still air)
const R_total_resistance = insulationRValue_h_ft2_F_Btu_in + R_surface_air; // Sum of resistances for 1 inch insulation
const U_overall_Btu_hr_ft2_F = 1 / R_total_resistance;

// Use inner diameter as a proxy for surface area for this simplified U-value calculation.
const heatTransferArea_ft2 = Math.PI * pipeDiameter_ft * loopLength_ft;

const total_Q_loss_Btu_hr = U_overall_Btu_hr_ft2_F * heatTransferArea_ft2 * tempDifference_water_ambient;

// --- 2. Required Flow Rate Calculation (GPM) ---
// Q_loss = m_dot * cp * deltaT => m_dot = Q_loss / (cp * deltaT)
const m_dot_lb_hr = total_Q_loss_Btu_hr / (cp_water * tempDrop_F);
const m_dot_lb_sec = m_dot_lb_hr / 3600;

// Volumetric flow rate
const volumetric_flow_ft3_sec = m_dot_lb_sec / rho_water_lb_ft3;
let requiredFlowRate_GPM = volumetric_flow_ft3_sec * 60 * gallons_per_ft3;

// Cap minimum flow rate to prevent calculation issues with very small or zero flow
const minFlowRate_GPM_cap = 0.05; // GPM (e.g., a very low but non-zero flow)
requiredFlowRate_GPM = Math.max(minFlowRate_GPM_cap, requiredFlowRate_GPM);

// --- 3. Head Loss Calculation (Darcy-Weisbach) ---
const pipeCrossSectionalArea_ft2 = Math.PI * Math.pow(pipeDiameter_ft / 2, 2);

// Calculate velocity (ft/sec)
const volumetric_flow_ft3_min = requiredFlowRate_GPM / gallons_per_ft3;
const velocity_ft_sec = (volumetric_flow_ft3_min / 60) / pipeCrossSectionalArea_ft2;

// Reynolds Number (Re)
const Re = (rho_water_lb_ft3 * velocity_ft_sec * pipeDiameter_ft) / mu_water_lb_ft_sec;

// Friction Factor (f) - Colebrook-White equation approximated by Swamee-Jain
let f;
if (Re < 2000) { // Laminar flow
    f = 64 / Re;
} else { // Turbulent flow (Re >= 2000, using Swamee-Jain for practical sizing)
    const epsilon_D = pipeRoughness_ft / pipeDiameter_ft;
    f = 0.25 / Math.pow(Math.log10((epsilon_D / 3.7) + (5.74 / Math.pow(Re, 0.9))), 2);
}

// Cap friction factor to reasonable range to prevent extreme values from calculation edge cases
f = Math.max(0.008, Math.min(0.1, f)); 

// Major Head Loss
const h_f_major_ft = f * (loopLength_ft / pipeDiameter_ft) * (Math.pow(velocity_ft_sec, 2) / (2 * g));

// Minor Head Loss (using equivalent length for fittings)
const K_total_from_equiv_L = f * (equivalentLengthFittings_ft / pipeDiameter_ft); // K-factor derived from equivalent length
const h_f_minor_ft = K_total_from_equiv_L * (Math.pow(velocity_ft_sec, 2) / (2 * g));

// Total Head Loss
let totalHeadLoss_ft = h_f_major_ft + h_f_minor_ft;

// Cap total head loss to a reasonable maximum for DHW systems
const maxHeadLoss_ft_cap = 200; // e.g., 200 ft is a practical upper limit for most DHW recirc systems
totalHeadLoss_ft = Math.min(maxHeadLoss_ft_cap, totalHeadLoss_ft);

// Return results
return {
    requiredFlowRate: requiredFlowRate_GPM,
    totalHeadLoss: totalHeadLoss_ft
};
