const airflowVolume = inputs.airflowVolume; // CFM
const totalStaticPressure = inputs.totalStaticPressure; // in. H2O
const fanStaticEfficiency = inputs.fanStaticEfficiency / 100; // decimal
const motorEfficiency = inputs.motorEfficiency / 100; // decimal
const altitude = inputs.altitude; // feet
const temperature = inputs.temperature; // °F

// Input validation
if (airflowVolume <= 0 || totalStaticPressure < 0 || fanStaticEfficiency <= 0 || motorEfficiency <= 0 || fanStaticEfficiency > 1 || motorEfficiency > 1) {
    return {
        airHorsepower: NaN,
        fanBrakeHorsepower: NaN,
        electricalPowerConsumption: NaN
    };
}

// Constants
const P_std_inHg = 29.921; // Standard barometric pressure at sea level (inHg)
const T_std_R = 70 + 459.67; // Standard temperature in Rankine (70°F)
const CONST_6356 = 6356; // Constant for AHP calculation (CFM, in. H2O, standard air)
const HP_TO_KW = 0.7457; // Conversion factor from hp to kW
const LAPSE_RATE_CONSTANT = 0.0000068755; // Constant for barometric pressure calculation
const PRESSURE_EXPONENT = 5.2559; // Exponent for barometric pressure calculation
const DENSITY_CONSTANT = 1.325; // Constant for air density calculation (lb/ft^3)
const ABS_ZERO_RANKINE = 459.67; // Conversion from °F to Rankine

// 1. Calculate actual air density and standard air density
// Standard air density (lb/ft^3)
const rho_std = DENSITY_CONSTANT * P_std_inHg / T_std_R;

// Barometric pressure at altitude (inHg) using ISA model approximation
const P_alt_inHg = P_std_inHg * Math.pow(Math.max(0, 1 - (LAPSE_RATE_CONSTANT * altitude)), PRESSURE_EXPONENT);

// Actual air temperature in Rankine
const T_actual_R = temperature + ABS_ZERO_RANKINE;

// Actual air density (lb/ft^3). Use Math.max(1, ...) to prevent division by zero or negative temperatures
const rho_actual = DENSITY_CONSTANT * P_alt_inHg / Math.max(1, T_actual_R);

// Density ratio (actual density / standard density). Cap to prevent extreme values.
const densityRatio = Math.max(0.5, Math.min(rho_actual / rho_std, 2.0)); 

// 2. Calculate Air Horsepower (AHP)
// AHP = (CFM * SP) / (Constant_6356 / densityRatio)  -- This formula is equivalent to (CFM * SP * densityRatio) / Constant_6356
const airHorsepower = (airflowVolume * totalStaticPressure * densityRatio) / CONST_6356;

// 3. Calculate Fan Brake Horsepower (BHP)
const fanBrakeHorsepower = airHorsepower / fanStaticEfficiency;

// 4. Calculate Electrical Power Consumption (kW)
const electricalPowerConsumption = (fanBrakeHorsepower * HP_TO_KW) / motorEfficiency;

return {
    airHorsepower: airHorsepower,
    fanBrakeHorsepower: fanBrakeHorsepower,
    electricalPowerConsumption: electricalPowerConsumption
};
