function calculateOutcomes(inputs) {
  const { childAgeMonths, childSocioeconomicStatus, preExistingDevelopmentalConcerns, programDurationMonths, teacherChildRatio, teacherQualificationLevel, curriculumFocus, parentInvolvementOpportunities } = inputs;

  // Base scores (neutral starting point)
  let academicScore = 50;
  let socialEmotionalScore = 50;

  // --- Child Characteristics Impact ---
  // SES: Significant positive impact on both
  academicScore += (childSocioeconomicStatus - 3) * 8;
  socialEmotionalScore += (childSocioeconomicStatus - 3) * 6;

  // Age at Entry: Optimal range (e.g., 36-48 months) gets slight boost, outside range minor penalty
  if (childAgeMonths >= 36 && childAgeMonths <= 48) {
    academicScore += 3;
    socialEmotionalScore += 2;
  } else if (childAgeMonths < 30 || childAgeMonths > 60) {
    academicScore -= 2;
    socialEmotionalScore -= 1;
  }

  // Pre-existing Developmental Concerns: Significant negative impact
  if (preExistingDevelopmentalConcerns === 1) {
    academicScore -= 15;
    socialEmotionalScore -= 12;
  }

  // --- Program Characteristics Impact ---
  // Program Duration: Positive impact, diminishing returns after 36 months
  academicScore += Math.min(programDurationMonths * 0.5, 18);
  socialEmotionalScore += Math.min(programDurationMonths * 0.4, 15);

  // Teacher-Child Ratio: Lower ratio (better) boosts scores, inverse relationship
  academicScore += (10 - teacherChildRatio) * 2;
  socialEmotionalScore += (10 - teacherChildRatio) * 2.5;

  // Teacher Qualification: Positive impact
  academicScore += (teacherQualificationLevel - 3) * 5;
  socialEmotionalScore += (teacherQualificationLevel - 3) * 4;

  // Curriculum Focus:
  // 1=Play-based: Stronger boost for social-emotional, moderate for academic
  // 2=Academic-focused: Stronger boost for academic, moderate for social-emotional
  // 3=Hybrid: Balanced boost
  if (curriculumFocus === 1) {
    academicScore += 5;
    socialEmotionalScore += 10;
  } else if (curriculumFocus === 2) {
    academicScore += 10;
    socialEmotionalScore += 5;
  } else if (curriculumFocus === 3) {
    academicScore += 7;
    socialEmotionalScore += 7;
  }

  // Parent Involvement: Positive impact
  academicScore += (parentInvolvementOpportunities - 3) * 4;
  socialEmotionalScore += (parentInvolvementOpportunities - 3) * 5;

  // --- Caps and Floors for Outcome Scores ---
  academicScore = Math.max(0, Math.min(100, academicScore));
  socialEmotionalScore = Math.max(0, Math.min(100, socialEmotionalScore));

  // --- Calculate Risk Factor Mitigation Index ---
  let riskMitigation = 0;
  // Base mitigation from program quality
  riskMitigation += (programDurationMonths / 60) * 20; // Up to 20 points for duration
  riskMitigation += ((12 - teacherChildRatio) / 8) * 25; // Up to 25 points for ratio
  riskMitigation += ((teacherQualificationLevel - 1) / 4) * 20; // Up to 20 points for qualification
  riskMitigation += ((parentInvolvementOpportunities - 1) / 4) * 15; // Up to 15 points for parent involvement

  // Enhanced mitigation for vulnerable children
  if (childSocioeconomicStatus < 3) {
    riskMitigation += (3 - childSocioeconomicStatus) * 5; // More mitigation credit for lower SES
  }
  if (preExistingDevelopmentalConcerns === 1) {
    riskMitigation += 10; // Extra mitigation for addressing concerns
  }

  riskMitigation = Math.max(0, Math.min(100, riskMitigation));

  // --- Calculate Long-Term Achievement Probability ---
  // This is a weighted average, with academic score having more weight for 'achievement'
  // and adjusted by how well risks are mitigated.
  let baseProbability = (academicScore * 0.6 + socialEmotionalScore * 0.4) * 0.8; // 80% contribution from direct scores
  let mitigationEffect = riskMitigation * 0.2; // 20% contribution from risk mitigation
  let longTermAchievementProbability = baseProbability + mitigationEffect;

  longTermAchievementProbability = Math.max(0, Math.min(100, longTermAchievementProbability));

  return {
    academicOutcomeScore: academicScore,
    socialEmotionalOutcomeScore: socialEmotionalScore,
    longTermAchievementProbability: longTermAchievementProbability,
    riskFactorMitigationIndex: riskMitigation
  };
}
