function calculate({ annualDemand, orderCost, holdingCostPerUnit, unitCost, leadTimeDays, safetyStockUnits }) {
    let eoq = 0;
    let totalAnnualOrderingCost = 0;
    let totalAnnualHoldingCost = 0;
    let totalAnnualInventoryCost = 0;
    let numberOfOrders = 0;
    let reorderPoint = null;

    if (annualDemand > 0 && orderCost > 0 && holdingCostPerUnit > 0) {
        eoq = Math.sqrt((2 * annualDemand * orderCost) / holdingCostPerUnit);

        // Ensure EOQ is at least 1 and a whole number for practical ordering
        eoq = Math.max(1, Math.round(eoq));

        totalAnnualOrderingCost = (annualDemand / eoq) * orderCost;
        totalAnnualHoldingCost = (eoq / 2) * holdingCostPerUnit;

        totalAnnualInventoryCost = totalAnnualOrderingCost + totalAnnualHoldingCost;
        if (unitCost !== null && unitCost !== undefined) {
             totalAnnualInventoryCost += annualDemand * unitCost;
        }

        numberOfOrders = annualDemand / eoq;
    }

    if (leadTimeDays !== null && leadTimeDays !== undefined && safetyStockUnits !== null && safetyStockUnits !== undefined && annualDemand > 0 && leadTimeDays >= 0 && safetyStockUnits >= 0) {
        const dailyDemand = annualDemand / 365; // Assuming 365 operating days for daily demand
        reorderPoint = (dailyDemand * leadTimeDays) + safetyStockUnits;
        reorderPoint = Math.round(reorderPoint); // Round to nearest unit
    }

    return {
        eoq: eoq,
        totalAnnualInventoryCost: totalAnnualInventoryCost,
        numberOfOrders: numberOfOrders,
        reorderPoint: reorderPoint
    };
}
